INSERT INTO `posts` (`id`, `user_id`, `topic_id`, `title`, `content`, `slug`, `image_path`, `IsPublished`, `views`, `create_date`, `update_date`) VALUES
(1, 1, 2, 'Sáng nay có 94 ca mắc Covid-19 lây nhiễm trong nước, 40 ca tại TP.HCM', '&lt;p&gt;&lt;strong&gt;S&amp;aacute;ng nay, 19.6,&amp;nbsp;&lt;a href=&quot;https://thanhnien.vn/bo-y-te-thong-bao/&quot; rel=&quot;&quot;&gt;Bộ Y tế th&amp;ocirc;ng b&amp;aacute;o&lt;/a&gt;&amp;nbsp;94&amp;nbsp;&lt;a href=&quot;https://thanhnien.vn/thoi-su/toi-nay-co-213-ca-mac-covdid-19-moi-them-303-ca-khoi-benh-1399410.html&quot; rel=&quot;&quot; target=&quot;_blank&quot;&gt;ca mắc Covid-19&lt;/a&gt;&amp;nbsp;mới. TP.HCM ghi nhận nhiều nhất với 40 ca.&amp;nbsp;&lt;a href=&quot;https://thanhnien.vn/da-nang/&quot; rel=&quot;&quot;&gt;Đ&amp;agrave; Nẵng&lt;/a&gt;&amp;nbsp;c&amp;oacute; 1 bệnh nh&amp;acirc;n sau hơn 14 ng&amp;agrave;y kh&amp;ocirc;ng c&amp;oacute; ca mắc mới.&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo th&amp;ocirc;ng b&amp;aacute;o của Bộ Y tế, từ 18 giờ ng&amp;agrave;y 18.6 đến 6 giờ s&amp;aacute;ng nay c&amp;oacute; 94 ca mắc Covid-19 mới do l&amp;acirc;y nhiễm trong nước, l&amp;agrave; c&amp;aacute;c bệnh nh&amp;acirc;n thứ 12.415 - 12.508 tại Việt Nam.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong đ&amp;oacute;, tại&amp;nbsp;&lt;a href=&quot;https://thanhnien.vn/thoi-su/tphcm-tim-nguoi-tung-den-5-cua-hang-thuoc-he-thong-hnam-mobile-1400761.html&quot; rel=&quot;&quot; target=&quot;_blank&quot;&gt;TP.HCM&lt;/a&gt;&amp;nbsp;c&amp;oacute; 40 ca, Bắc Ninh 15 ca, Nghệ An 13 ca, B&amp;igrave;nh Dương 12 ca, Bắc Giang 9 ca, Tiền Giang 3 ca, H&amp;agrave; Tĩnh 1 ca v&amp;agrave; Đ&amp;agrave; Nẵng 1 ca. 86/94 ca được ph&amp;aacute;t hiện trong khu c&amp;aacute;ch ly hoặc khu đ&amp;atilde; được phong toả, 8 ca được ph&amp;aacute;t hiện trong cộng đồng đang điều tra dịch tễ.&lt;/p&gt;\r\n\r\n&lt;p&gt;Cụ thể, 13 ca tại Nghệ An gồm 1 ca li&amp;ecirc;n quan đến Khu c&amp;ocirc;ng nghiệp Quang Ch&amp;acirc;u (Bắc Giang), 3 ca l&amp;agrave; c&amp;aacute;c trường hợp F1, 4 ca li&amp;ecirc;n quan đến H.Diễn Ch&amp;acirc;u v&amp;agrave; 5 ca đang điều tra dịch tễ.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong 15 bệnh nh&amp;acirc;n tại Bắc Ninh, 3 ca li&amp;ecirc;n quan đến ổ dịch Khu c&amp;ocirc;ng nghiệp Quế V&amp;otilde;, 5 ca li&amp;ecirc;n quan đến ổ dịch&amp;nbsp;&lt;a href=&quot;https://thanhnien.vn/thoi-su/pho-thu-tuong-khong-de-dich-choc-thung-cac-khu-cong-nghiep-1382539.html&quot; rel=&quot;&quot; target=&quot;_blank&quot;&gt;khu c&amp;ocirc;ng nghiệp&lt;/a&gt;&amp;nbsp;Khắc Niệm, 5 ca li&amp;ecirc;n quan đến ổ dịch Đại Ph&amp;uacute;c, 1 ca l&amp;agrave; F1 v&amp;agrave; 1 ca đang điều tra dịch tễ.&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Bệnh nh&amp;acirc;n 12424 ghi nhận tại H&amp;agrave; Tĩnh (nam, 61 tuổi, địa chỉ tại TP.H&amp;agrave; Tĩnh) l&amp;agrave; F1 của c&amp;aacute;c bệnh nh&amp;acirc;n 8425 v&amp;agrave; 8427, đ&amp;atilde; được c&amp;aacute;ch ly. Kết quả x&amp;eacute;t nghiệm ng&amp;agrave;y 18.6 x&amp;aacute;c định bệnh nh&amp;acirc;n dương t&amp;iacute;nh với&amp;nbsp;&lt;a href=&quot;https://thanhnien.vn/sars-cov-2/&quot; rel=&quot;&quot;&gt;SARS-CoV-2&lt;/a&gt;. Hiện, bệnh nh&amp;acirc;n được c&amp;aacute;ch ly, điều trị tại Bệnh viện đa khoa H.Kỳ Anh (H&amp;agrave; Tĩnh).&lt;/p&gt;\r\n\r\n&lt;p&gt;Bệnh nh&amp;acirc;n 12437 ghi nhận tại Đ&amp;agrave; Nẵng (nam, 59 tuổi, địa chỉ tại Q.Thanh Kh&amp;ecirc;, Đ&amp;agrave; Nẵng) đang được điều tra dịch tễ.&lt;/p&gt;\r\n\r\n&lt;p&gt;9 ca tại Bắc Giang đều trong khu c&amp;aacute;ch ly v&amp;agrave; khu vực đ&amp;atilde; được phong tỏa, li&amp;ecirc;n quan đến c&amp;ocirc;ng nh&amp;acirc;n l&amp;agrave;m tại c&amp;aacute;c Khu c&amp;ocirc;ng nghiệp, c&amp;oacute; kết quả x&amp;eacute;t nghiệm dương t&amp;iacute;nh với SARS-CoV-2.&lt;/p&gt;\r\n\r\n&lt;p&gt;12 ca ghi nhận tại B&amp;igrave;nh Dương l&amp;agrave; c&amp;aacute;c trường hợp F1, đ&amp;atilde; được c&amp;aacute;ch ly.&lt;/p&gt;\r\n\r\n&lt;p&gt;3 ca tại Tiền Giang đều ghi nhận trong khu vực phong tỏa, li&amp;ecirc;n quan đến bệnh nh&amp;acirc;n 10630. Kết quả x&amp;eacute;t nghiệm ng&amp;agrave;y 17.6 x&amp;aacute;c định c&amp;aacute;c bệnh nh&amp;acirc;n dương t&amp;iacute;nh với SARS-CoV-2. Hiện, c&amp;aacute;c bệnh nh&amp;acirc;n được c&amp;aacute;ch ly, điều trị tại Bệnh viện Lao v&amp;agrave; bệnh phổi tỉnh Tiền Giang.&lt;/p&gt;\r\n\r\n&lt;p&gt;40 ca tại TP.HCM gồm 38 ca l&amp;agrave; c&amp;aacute;c trường hợp F1 đ&amp;atilde; được c&amp;aacute;ch ly, 1 ca li&amp;ecirc;n quan đến điểm nh&amp;oacute;m Hội th&amp;aacute;nh truyền gi&amp;aacute;o Phục Hưng v&amp;agrave; 1 ca li&amp;ecirc;n quan đến trụ sở UBND Q.7, TP.HCM.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo Bộ Y tế, trong đợt dịch thứ 4 tại Việt Nam (từ ng&amp;agrave;y 27.4 đến nay) c&amp;oacute; 9.266 ca mắc Covid-19, trong đ&amp;oacute; 1.938 bệnh nh&amp;acirc;n đ&amp;atilde; được c&amp;ocirc;ng bố khỏi bệnh.&lt;/p&gt;\r\n\r\n&lt;p&gt;Từ đầu dịch (th&amp;aacute;ng 1.2020) đến nay, Việt Nam c&amp;oacute; 10.836 ca ghi nhận trong nước v&amp;agrave; 1.672 ca&amp;nbsp;&lt;a href=&quot;https://thanhnien.vn/thoi-su/hai-ca-nhap-canh-trai-phep-duong-tinh-covid-19-tai-hai-phong-va-tpho-chi-minh-1359381.html&quot; rel=&quot;&quot; target=&quot;_blank&quot;&gt;nhập cảnh&lt;/a&gt;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hiện c&amp;oacute; 23 tỉnh gồm: Y&amp;ecirc;n B&amp;aacute;i, Quảng Ng&amp;atilde;i, Đồng Nai, Quảng Ninh, Quảng Nam, Quảng Trị, Thừa Thi&amp;ecirc;n - Huế, Nam Định, Tuy&amp;ecirc;n Quang, Sơn La, Ninh B&amp;igrave;nh, Thanh H&amp;oacute;a, Th&amp;aacute;i Nguy&amp;ecirc;n, Hưng Y&amp;ecirc;n, Hải Ph&amp;ograve;ng, T&amp;acirc;y Ninh, Gia Lai, Bạc Li&amp;ecirc;u, Điện Bi&amp;ecirc;n, Đắk Lắk, Đồng Th&amp;aacute;p, Tr&amp;agrave; Vinh v&amp;agrave; Th&amp;aacute;i B&amp;igrave;nh đ&amp;atilde; qua 14 ng&amp;agrave;y kh&amp;ocirc;ng ghi nhận trường hợp mắc mới trong cộng đồng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Từ ng&amp;agrave;y 29.4 đến nay hệ thống y tế đ&amp;atilde; thực hiện hơn 2,3 triệu mẫu x&amp;eacute;t nghiệm s&amp;agrave;ng lọc v&amp;agrave; x&amp;aacute;c định ca mắc Covid-19.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://image.thanhnien.vn/2048/uploaded/lienchau/2021_06_19/thanhnien2khuyencaotno_ixse.jpg&quot; style=&quot;height:618px; width:600px&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;(&lt;em&gt;Nguồn: Li&amp;ecirc;n Ch&amp;acirc;u -&amp;nbsp;Thanh Ni&amp;ecirc;n Online)&lt;/em&gt;&lt;/p&gt;\r\n', 'sang-nay-co-94-ca-mac-covid-19-lay-nhiem-trong-nuoc-40-ca-tai-tphcm', 'https://image.thanhnien.vn/uploaded/lienchau/2021_06_19/196-tno_gdnm.gif', 1, 37, '2021-06-19 08:32:45', '2021-06-28 17:18:47'),
(2, 1, 2, 'TP.HCM: Tìm người đến chợ Bà Chiểu liên quan ca dương tính Covid-19', '&lt;p&gt;&lt;strong&gt;UBND P.1, Q.B&amp;igrave;nh Thạnh (TP.HCM) vừa ra th&amp;ocirc;ng b&amp;aacute;o t&amp;igrave;m người đến sạp số 1461 đường Ng&amp;ocirc; Nhơn Tịnh, chợ B&amp;agrave; Chiểu do c&amp;oacute; li&amp;ecirc;n quan ca dương t&amp;iacute;nh&amp;nbsp;&lt;a href=&quot;https://thanhnien.vn/covid-19/&quot; rel=&quot;&quot;&gt;Covid-19&lt;/a&gt;.&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Tối 26.6, đại diện UBND P.1, Q.B&amp;igrave;nh Thạnh cho biết đ&amp;atilde; ra th&amp;ocirc;ng b&amp;aacute;o t&amp;igrave;m người đ&amp;atilde; đến chợ B&amp;agrave; Chiểu do li&amp;ecirc;n quan ca dương t&amp;iacute;nh&amp;nbsp;&lt;a href=&quot;https://thanhnien.vn/thoi-su/tinh-hinh-covid-19-toi-266-tphcm-ghi-nhan-58-ca-nhiem-moi-nhieu-nhat-ca-nuoc-1404833.html&quot; rel=&quot;&quot; target=&quot;_blank&quot;&gt;Covid-19&amp;nbsp;&lt;/a&gt;tại đ&amp;acirc;y.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo th&amp;ocirc;ng b&amp;aacute;o, UBND P.1, Q.B&amp;igrave;nh Thạnh đ&amp;atilde; tạm thời đ&amp;oacute;ng cửa c&amp;aacute;c sạp từ số 1451 đến 1471 tr&amp;ecirc;n đường Ng&amp;ocirc; Nhơn Tịnh, chợ B&amp;agrave; Chiểu từ ng&amp;agrave;y 25.6 cho đến khi c&amp;oacute; th&amp;ocirc;ng&amp;nbsp;&lt;a href=&quot;https://thanhnien.vn/&quot; rel=&quot;nofollow&quot; target=&quot;_blank&quot;&gt;b&amp;aacute;o mới&lt;/a&gt;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Người d&amp;acirc;n từng đến mua sắm tại sạp số 1461 đường Ng&amp;ocirc; Nhơn Tịnh, chợ B&amp;agrave; Chiểu theo khung giờ từ 8 - 16 giờ, từ ng&amp;agrave;y 12 - 25.6 cần li&amp;ecirc;n hệ ngay Trạm y tế P.1, Q.B&amp;igrave;nh Thạnh (địa chỉ số 54&amp;nbsp;&lt;a href=&quot;https://thanhnien.vn/thoi-su/tphcm-xu-phat-nguoi-mua-lan-nguoi-ban-o-cho-tu-phat-sau-chi-thi-10-1401886.html&quot; rel=&quot;&quot; target=&quot;_blank&quot;&gt;Vũ T&amp;ugrave;ng&lt;/a&gt;, P.1, Q.B&amp;igrave;nh Thạnh)&amp;nbsp;&lt;a href=&quot;https://thanhnien.vn/thoi-su/tphcm-de-xuat-khai-bao-y-te-dien-tu-toan-dan-tu-ngay-246-1402276.html&quot; rel=&quot;&quot; target=&quot;_blank&quot;&gt;để khai b&amp;aacute;o y tế&lt;/a&gt;&amp;nbsp;v&amp;agrave; lấy mẫu x&amp;eacute;t nghiệm.&lt;/p&gt;\r\n\r\n&lt;p&gt;Như&amp;nbsp;&lt;em&gt;Thanh Ni&amp;ecirc;n&amp;nbsp;&lt;/em&gt;đ&amp;atilde; th&amp;ocirc;ng tin, v&amp;agrave;o chều 26.6, lực lượng chức năng&amp;nbsp;&lt;a href=&quot;https://thanhnien.vn/thoi-su/lay-mau-xet-nghiem-covid-19-vi-ca-nghi-nhiem-la-nguoi-phu-quan-o-cho-ba-chieu-1404823.html&quot; rel=&quot;&quot; target=&quot;_blank&quot;&gt;tạm phong tỏa khu vực chợ B&amp;agrave; Chiểu&lt;/a&gt;&amp;nbsp;để lấy mẫu x&amp;eacute;t nghiệm Covid-19 tầm so&amp;aacute;t cho tiểu thương, do li&amp;ecirc;n quan 1 ca nghi nhiễm Covid-19.&lt;/p&gt;\r\n\r\n&lt;p&gt;Đại diện l&amp;atilde;nh đạo UBND P.1 (Q.B&amp;igrave;nh Thạnh) x&amp;aacute;c nhận c&amp;oacute; ca nghi nhiễm Covid-19 (đ&amp;atilde; c&amp;aacute;ch ly tập trung từ trước) ở khu vực chợ B&amp;agrave; Chiểu. Hiện ch&amp;iacute;nh quyền địa phương đang phối hợp lực lượng y tế lấy mẫu x&amp;eacute;t nghiệm tầm so&amp;aacute;t; đồng thời tiến h&amp;agrave;nh điều tra dịch tễ.&lt;/p&gt;\r\n\r\n&lt;p&gt;Đến tối ng&amp;agrave;y 26.6, đại diện ban quản l&amp;yacute; chợ B&amp;agrave; Chiểu cho biết việc x&amp;eacute;t nghiệm Covid-19 đ&amp;atilde; ho&amp;agrave;n tất l&amp;uacute;c 19 giờ 30 c&amp;ugrave;ng ng&amp;agrave;y với 736 mẫu x&amp;eacute;t nghiệm. Dự kiến, ng&amp;agrave;y mai (27.6), chợ B&amp;agrave; Chiểu vẫn hoạt động b&amp;igrave;nh thường.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;(Song Mai - Thanh Ni&amp;ecirc;n Online)&lt;/p&gt;\r\n', 'tphcm-tim-nguoi-den-cho-ba-chieu-lien-quan-ca-duong-tinh-covid-19', 'https://image.thanhnien.vn/660x370/uploaded/maipts/2021_06_26/chobachieu_hznc.jpg', 1, 18, '2021-06-27 00:27:48', '2021-06-28 18:23:45'),
(3, 1, 3, 'Mỹ đưa tàu khu trục tên lửa dẫn đường tới Ukraine', '&lt;h3&gt;(NLĐO) - T&amp;agrave;u khu trục t&amp;ecirc;n lửa dẫn đường của Mỹ USS Ross đ&amp;atilde; cập cảng Odessa của Ukraine để tham gia cuộc tập trận h&amp;agrave;ng hải đa phương Sea Breeze 2021 ở biển Đen.&lt;/h3&gt;\r\n\r\n&lt;p&gt;Theo đ&amp;agrave;i Sputnik,&amp;nbsp;Trung t&amp;acirc;m Quản l&amp;yacute; Quốc ph&amp;ograve;ng Nga trước đ&amp;oacute; cho biết hải qu&amp;acirc;n nước n&amp;agrave;y đang theo d&amp;otilde;i t&amp;agrave;u&amp;nbsp;USS Ross sau khi n&amp;oacute; v&amp;agrave;o biển Đen ng&amp;agrave;y 26-6.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ng&amp;agrave;y 28-6,&amp;nbsp;t&amp;agrave;u USS Ross được nh&amp;igrave;n thấy cập cảng&amp;nbsp;Odessa của Ukraine để chuẩn bị tham gia cuộc tập trận thường ni&amp;ecirc;n&amp;nbsp;Sea Breeze 2021. Đ&amp;acirc;y l&amp;agrave; lần thứ&amp;nbsp;21 cuộc tập trận n&amp;agrave;y được tổ chức. Lực lượng đến từ hơn&amp;nbsp;30 quốc gia sẽ tham gia đ&amp;agrave;o tạo khả năng tương t&amp;aacute;c ở biển Đen.&lt;/p&gt;\r\n\r\n&lt;p&gt;Cuộc tập trận&amp;nbsp;Sea Breeze năm nay dự kiến k&amp;eacute;o d&amp;agrave;i 13 ng&amp;agrave;y,&amp;nbsp;khởi động v&amp;agrave;o ng&amp;agrave;y 28-6. Khoảng 5.000 qu&amp;acirc;n nh&amp;acirc;n v&amp;agrave; hơn 30 t&amp;agrave;u từ Mỹ, Ukraine, Anh, Canada, H&amp;agrave; Lan, Bulgaria, Hy Lạp, Latvia v&amp;agrave; c&amp;aacute;c quốc gia kh&amp;aacute;c sẽ tham dự.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://nld.mediacdn.vn/thumb_w/684/291774122806476800/2021/6/28/1-16248768237741867370587.jpg&quot; style=&quot;height:318px; width:500px&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://nld.mediacdn.vn/291774122806476800/2021/6/28/2-1624878108884155014702.jpg&quot; style=&quot;height:668px; width:500px&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Sea Breeze&amp;nbsp;được tổ chức tại Ukraine từ năm 1997 theo bản ghi nhớ v&amp;agrave; hợp t&amp;aacute;c qu&amp;acirc;n sự giữa Lầu Năm G&amp;oacute;c v&amp;agrave; Bộ Quốc ph&amp;ograve;ng Ukraine. Cuộc tập trận&amp;nbsp;hải qu&amp;acirc;n n&amp;agrave;y li&amp;ecirc;n quan tới một loạt quốc gia th&amp;agrave;nh vi&amp;ecirc;n Tổ chức Hiệp ước Bắc Đại T&amp;acirc;y Dương (NATO) v&amp;agrave; c&amp;aacute;c đồng minh, diễn ra ở biển Đen.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trước khi khởi động cuộc tập trận&amp;nbsp;Sea Breeze, một sự cố được ghi nhận ở biển Đen giữa lực lượng Nga v&amp;agrave; Anh. Cụ thể, ng&amp;agrave;y 23-6, Bộ Quốc ph&amp;ograve;ng Nga tuy&amp;ecirc;n bố một t&amp;agrave;u tuần tra v&amp;agrave; một chiến đấu cơ Su-24M của họ đ&amp;atilde; &amp;quot;bắn cảnh c&amp;aacute;o v&amp;agrave; thả bom gần t&amp;agrave;u chiến&amp;nbsp;HMS Defender của hải qu&amp;acirc;n Ho&amp;agrave;ng gia Anh&amp;quot; .&lt;/p&gt;\r\n\r\n&lt;p&gt;Moscow c&amp;aacute;o buộc t&amp;agrave;u chiến Anh hoạt động ở v&amp;ugrave;ng biển c&amp;aacute;ch căn cứ Nga tại TP&amp;nbsp;Sevastopol khoảng 3 km. Tuy nhi&amp;ecirc;n,&amp;nbsp;Bộ Quốc ph&amp;ograve;ng Anh khẳng định &amp;quot;kh&amp;ocirc;ng c&amp;oacute; sự cố n&amp;agrave;o như vậy&amp;quot;, đồng thời khẳng định t&amp;agrave;u&amp;nbsp;HMS Defende &amp;quot;chỉ đi qua l&amp;atilde;nh hải Ukraine dựa tr&amp;ecirc;n luật ph&amp;aacute;p quốc tế&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong khi đ&amp;oacute;, một nh&amp;agrave; b&amp;aacute;o của đ&amp;agrave;i BBC (Anh) c&amp;oacute; mặt tr&amp;ecirc;n t&amp;agrave;u&amp;nbsp;HMS Defende x&amp;aacute;c nhận t&amp;agrave;u Nga di chuyển xung quanh t&amp;agrave;u chiến Anh v&amp;agrave; &amp;ocirc;ng &amp;quot;nghe thấy cả tiếng s&amp;uacute;ng&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ngay sau đ&amp;oacute;, Bộ Quốc ph&amp;ograve;ng Nga y&amp;ecirc;u cầu Lầu Năm G&amp;oacute;c v&amp;agrave; chỉ huy lực lượng hải qu&amp;acirc;n Ho&amp;agrave;ng gia Anh tr&amp;aacute;nh những h&amp;agrave;nh động g&amp;acirc;y nguy hiểm tương tự ở biển Đen.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;&lt;strong&gt;Phạm Nghĩa&lt;/strong&gt;&lt;/p&gt;\r\n', 'my-dua-tau-khu-truc-ten-lua-dan-duong-toi-ukraine', './uploads/images/1624883251_ship.jpg', 1, 4, '2021-06-28 19:27:32', '2021-06-28 16:22:36'),
(4, 1, 24, 'Dòng tiền quay lại chứng khoán, VN-Index trụ vững mốc 1.400 điểm', '&lt;h3&gt;(NLĐO) - Đ&amp;oacute;ng cửa phi&amp;ecirc;n giao dịch chứng kho&amp;aacute;n đầu tuần (28-6), nh&amp;agrave; đầu tư hưng phấn khi VN-Index đ&amp;atilde; trụ vững mức 1.400 điểm, đồng thời gi&amp;aacute; trị giao dịch cũng tăng cao so với những phi&amp;ecirc;n trước đ&amp;oacute;.&lt;/h3&gt;\r\n\r\n&lt;p&gt;Cụ thể, kết th&amp;uacute;c ng&amp;agrave;y giao dịch, VN-Index tăng 15,69 điểm, l&amp;ecirc;n 1.405,81 điểm. Trong khi đ&amp;oacute;, VN30- Index tăng mạnh 21,11 điểm, l&amp;ecirc;n 1.521,41 điểm. HNX-Index tăng 4,88 điểm, l&amp;ecirc;n 323,1 điểm. UPCoM-Index cũng quay đầu tăng nhẹ 0,32 điểm, l&amp;ecirc;n 89,8 điểm.&lt;/p&gt;\r\n\r\n&lt;p&gt;Đ&amp;aacute;ng ch&amp;uacute; &amp;yacute; phi&amp;ecirc;n n&amp;agrave;y l&amp;agrave; thanh khoản thị trường đ&amp;atilde; tăng mạnh trở lại, tạo t&amp;acirc;m l&amp;yacute; hưng phấn cho nh&amp;agrave; đầu tư sau 1 tuần giao dịch giảm mạnh. Theo đ&amp;oacute;, tổng gi&amp;aacute; trị giao dịch to&amp;agrave;n thị trường đạt 27.670 tỉ đồng, tăng kh&amp;aacute; nhiều so với phi&amp;ecirc;n cuối tuần trước. Trong đ&amp;oacute;, giao dịch khớp lệnh chiếm 25.800 tỉ đồng ri&amp;ecirc;ng HoSE l&amp;agrave; 21.500 tỉ đồng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Mặc d&amp;ugrave; vậy, nh&amp;agrave; đầu tư nước ngo&amp;agrave;i vẫn b&amp;aacute;n r&amp;ograve;ng hơn 200 tỉ đồng tr&amp;ecirc;n HoSE trong khi mua r&amp;ograve;ng nhẹ ở HNX v&amp;agrave; UPCoM. Trong phi&amp;ecirc;n giao n&amp;agrave;y, c&amp;aacute;c cổ phiếu ng&amp;agrave;nh t&amp;agrave;i ch&amp;iacute;nh, ng&amp;acirc;n h&amp;agrave;ng tăng mạnh như MSB, TPB, TCB, GVR, BVH...&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://nld.mediacdn.vn/thumb_w/684/291774122806476800/2021/6/28/img-9473-16248785557291407926861.jpg&quot; style=&quot;height:375px; width:500px&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong khi đ&amp;oacute;, mới mở cửa thị trường, VN-Index đ&amp;atilde; bứt ph&amp;aacute; nhưng khi gần chạm ngưỡng 1.400 điểm th&amp;igrave; giằng co. Sau đ&amp;oacute;, gần cuối phi&amp;ecirc;n s&amp;aacute;ng mới vượt qua ngưỡng 1.400 điểm.&lt;/p&gt;\r\n\r\n&lt;p&gt;Đ&amp;oacute;ng cửa phi&amp;ecirc;n s&amp;aacute;ng, VN-Index tăng 12,41 điểm (0,89%) l&amp;ecirc;n 1.402,53 điểm. To&amp;agrave;n s&amp;agrave;n c&amp;oacute; 207 m&amp;atilde; tăng, 177 m&amp;atilde; giảm v&amp;agrave; 43 m&amp;atilde; đứng gi&amp;aacute;.&lt;/p&gt;\r\n\r\n&lt;p&gt;HNX-Index cũng tăng 2,72 điểm (0,85%) l&amp;ecirc;n 320,94 điểm. To&amp;agrave;n s&amp;agrave;n c&amp;oacute; 93 m&amp;atilde; tăng, 103 m&amp;atilde; giảm v&amp;agrave; 64 m&amp;atilde; đứng gi&amp;aacute;. Ngược lại, UPCoM-Index giảm 0,12 điểm (-0,13%) xuống 89,36 điểm.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nhận định phi&amp;ecirc;n h&amp;ocirc;m nay, &amp;ocirc;ng Huỳnh Anh Tuấn, Tổng Gi&amp;aacute;m đốc C&amp;ocirc;ng ty chứng kho&amp;aacute;n Đ&amp;ocirc;ng &amp;Aacute; (DAS), cho rằng với sự thu h&amp;uacute;t d&amp;ograve;ng tiền như phi&amp;ecirc;n đầu tuần, đ&amp;atilde; k&amp;eacute;o VN-Index l&amp;ecirc;n ngưỡng quan trọng 1.400 điểm. Điều n&amp;agrave;y đ&amp;atilde; tạo t&amp;acirc;m l&amp;yacute; tốt cho nh&amp;agrave; đầu tư. Điều quan trọng l&amp;agrave; kết quả kinh doanh của c&amp;aacute;c ng&amp;acirc;n h&amp;agrave;ng 6 th&amp;aacute;ng đ&amp;atilde; tốt hơn năm ngo&amp;aacute;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Đồng thời thị trường kh&amp;ocirc;ng chỉ tập trung v&amp;agrave;o 1 v&amp;agrave;i m&amp;atilde; blueships m&amp;agrave; đồng loạt tăng gi&amp;aacute; ở nhiều m&amp;atilde; chứng kho&amp;aacute;n. Đ&amp;oacute; l&amp;agrave; l&amp;yacute; do c&amp;oacute; thể kỳ vọng sự bền vững của thị trường trong những phi&amp;ecirc;n tới.&lt;/p&gt;\r\n\r\n&lt;p&gt;Đặc biệt với th&amp;ocirc;ng tin hệ thống giao dịch mới tr&amp;ecirc;n HoSE do FPT triển khai sẽ vận h&amp;agrave;nh v&amp;agrave;o th&amp;aacute;ng 7 n&amp;agrave;y cũng khiến nh&amp;agrave; đầu tư kỳ vọng k&amp;eacute;o lại d&amp;ograve;ng tiền của nh&amp;agrave; đầu tư nước ngo&amp;agrave;i.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;&lt;strong&gt;Tin, ảnh: Sơn Nhung&lt;/strong&gt;&lt;/p&gt;\r\n', 'dong-tien-quay-lai-chung-khoan-vn-index-tru-vung-moc-1400-diem', './uploads/images/1624897784_img-9473-16248785557291407926861.jpg', 1, 1, '2021-06-28 23:29:44', '2021-06-28 16:37:54'),
(5, 4, 5, 'Người đàn ông mất trí nhớ cầu hôn lại vợ mình', '&lt;p&gt;&amp;Ocirc;ng Peter, 56 tuổi, ở bang Connecticut bị bệnh Alzheimer n&amp;ecirc;n qu&amp;ecirc;n cả việc bản th&amp;acirc;n đ&amp;atilde; kết h&amp;ocirc;n 12 năm, nhưng &amp;ocirc;ng vẫn y&amp;ecirc;u v&amp;agrave; cầu h&amp;ocirc;n, cưới lại ch&amp;iacute;nh vợ m&amp;igrave;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;V&amp;agrave;o một tối thứ bảy, khi đang ngồi xem tivi, &amp;ocirc;ng Peter bỗng nh&amp;igrave;n sang vợ m&amp;igrave;nh - b&amp;agrave; Lisa - rồi bất ngờ cầu h&amp;ocirc;n. Tr&amp;iacute; nhớ của Peter v&amp;agrave; khả năng n&amp;oacute;i đều bị ảnh hưởng bởi căn bệnh Alzheimer ba năm trước. Trong thời điểm n&amp;agrave;y &amp;ocirc;ng ho&amp;agrave;n to&amp;agrave;n đối xử với vợ như người lạ.&lt;/p&gt;\r\n\r\n&lt;p&gt;Peter v&amp;agrave; Lisa&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/tag/ket-hon-737379-p2&quot; rel=&quot;dofollow&quot; target=&quot;_blank&quot;&gt;kết h&amp;ocirc;n&lt;/a&gt;&amp;nbsp;c&amp;aacute;ch đ&amp;acirc;y 12 năm. Họ từng l&amp;agrave; h&amp;agrave;ng x&amp;oacute;m của nhau. Sau n&amp;agrave;y hai nh&amp;agrave; chuyển đến hai bang kh&amp;aacute;c nhau n&amp;ecirc;n họ mất li&amp;ecirc;n lạc v&amp;agrave; chỉ t&amp;igrave;nh cờ gặp lại khi cả hai đều đ&amp;atilde; ly h&amp;ocirc;n. Họ duy tr&amp;igrave; y&amp;ecirc;u xa trong 8 năm đến khi c&amp;aacute;c con đều đ&amp;atilde; v&amp;agrave;o đại học.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://i1-giadinh.vnecdn.net/2021/06/27/vo-chong-2-3394-1624784199.jpg?w=1020&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=fio9I-7NPHwS_AnLs4XBsA&quot; style=&quot;height:337px; width:600px&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-size:9px&quot;&gt;&amp;Ocirc;ng Peter v&amp;agrave; b&amp;agrave; Lisa trong ng&amp;agrave;y cưới. Ảnh:&amp;nbsp;&lt;em&gt;CNN&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Năm 2009, b&amp;agrave; Lisa dọn đến Connecticut v&amp;agrave; l&amp;agrave;m đ&amp;aacute;m cưới. Th&amp;aacute;ng 4/2018, &amp;ocirc;ng Peter được x&amp;aacute;c định mắc&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/topic/benh-alzheimer-20187&quot; rel=&quot;dofollow&quot; target=&quot;_blank&quot;&gt;bệnh Alzheimer&lt;/a&gt;. Khi bắt đầu suy giảm tr&amp;iacute; nhớ, &amp;ocirc;ng qu&amp;ecirc;n v&amp;iacute; tiền, ch&amp;igrave;a kh&amp;oacute;a rồi dần qu&amp;ecirc;n c&amp;aacute;ch đọc s&amp;aacute;ch cũng như &amp;yacute; nghĩa của c&amp;aacute;c từ, tiếp đ&amp;oacute; l&amp;agrave; kh&amp;ocirc;ng thể n&amp;oacute;i trọn một c&amp;acirc;u. V&amp;agrave;i th&amp;aacute;ng sau, &amp;ocirc;ng qu&amp;ecirc;n lu&amp;ocirc;n cả Lisa l&amp;agrave; vợ m&amp;igrave;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Đ&amp;oacute; l&amp;agrave; thời điểm ch&amp;uacute;ng t&amp;ocirc;i đi du lịch c&amp;ugrave;ng nhau đến đảo Rhode Island&amp;quot;, Lisa n&amp;oacute;i. Khi trở về nh&amp;agrave;, Peter l&amp;uacute;c đ&amp;oacute; ngồi ghế phụ li&amp;ecirc;n tục chỉ đường cho vợ v&amp;agrave; n&amp;oacute;i những c&amp;acirc;u như một người xa lạ. &amp;quot;Đ&amp;oacute; l&amp;agrave; đường về nh&amp;agrave; t&amp;ocirc;i. Rẽ phải l&amp;agrave; tới&amp;quot;, người đ&amp;agrave;n &amp;ocirc;ng 60 tuổi n&amp;oacute;i với vợ m&amp;igrave;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;Lisa cho biết, từ khi chồng mất tr&amp;iacute; nhớ, họ c&amp;oacute; th&amp;ecirc;m những kỷ niệm mới b&amp;ecirc;n nhau. &amp;quot;Người ta thường n&amp;oacute;i rằng thật buồn v&amp;agrave; kh&amp;oacute; chịu khi sống c&amp;ugrave;ng người mắc bệnh Alzheimer. Nhưng sự thật l&amp;agrave; c&amp;oacute; rất nhiều niềm vui, v&amp;igrave; ch&amp;uacute;ng t&amp;ocirc;i vẫn lu&amp;ocirc;n ở b&amp;ecirc;n nhau&amp;quot;, b&amp;agrave; chia sẻ. Tuy vậy, đ&amp;ocirc;i l&amp;uacute;c người phụ nữ 54 tuổi cũng thấy buồn v&amp;igrave; trong khi b&amp;agrave; c&amp;ograve;n nhớ những kỷ niệm cũ, c&amp;ograve;n chồng m&amp;igrave;nh th&amp;igrave; kh&amp;ocirc;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tưởng rằng Peter sẽ mất tr&amp;iacute; nhớ cả đời, nhưng v&amp;agrave;o cuối th&amp;aacute;ng 12 năm ngo&amp;aacute;i, khi c&amp;ugrave;ng xem chương tr&amp;igrave;nh ti vi y&amp;ecirc;u th&amp;iacute;ch, thấy b&amp;agrave; kh&amp;oacute;c, &amp;ocirc;ng đ&amp;atilde; đề nghị họ cưới nhau.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Bạn kh&amp;ocirc;ng thể n&amp;oacute;i: Ch&amp;uacute;ng m&amp;igrave;nh đ&amp;atilde; cưới rồi m&amp;agrave;. V&amp;igrave; thế t&amp;ocirc;i trả lời: Tất nhi&amp;ecirc;n l&amp;agrave; em đồng &amp;yacute;&amp;quot;, Lisa n&amp;oacute;i, trong khi &amp;ocirc;ng Peter rất h&amp;aacute;o hức giục vợ phải chuẩn bị nhiều thứ cho đ&amp;aacute;m cưới.&lt;/p&gt;\r\n\r\n&lt;p&gt;Lisa gọi điện hỏi con c&amp;aacute;i xem liệu c&amp;oacute; ai sẵn s&amp;agrave;ng tới ch&amp;uacute;c ph&amp;uacute;c v&amp;agrave; l&amp;agrave;m chứng cho đ&amp;aacute;m cưới lại của m&amp;igrave;nh kh&amp;ocirc;ng v&amp;agrave; tất cả đều ủng hộ. &amp;quot;Căn bệnh mất tr&amp;iacute; nhớ kh&amp;oacute; đo&amp;aacute;n n&amp;ecirc;n mỗi gi&amp;acirc;y ph&amp;uacute;t t&amp;ocirc;i đều hồi hộp v&amp;agrave; cảm thấy n&amp;oacute; mầu nhiệm&amp;quot;, Lisa n&amp;oacute;i. D&amp;ugrave; căn bệnh c&amp;oacute; thể khiến người chồng qu&amp;ecirc;n mất vợ bất cứ l&amp;uacute;c n&amp;agrave;o nhưng người phụ nữ n&amp;agrave;y kh&amp;ocirc;ng lo lắng, bởi b&amp;agrave; biết m&amp;igrave;nh l&amp;agrave; vợ &amp;ocirc;ng v&amp;agrave; y&amp;ecirc;u &amp;ocirc;ng, thế l&amp;agrave; đủ.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ng&amp;agrave;y 26/4/2021, Peter v&amp;agrave; Lisa l&amp;agrave;m lễ cưới lần hai v&amp;agrave; trao lại lời thề nguyện. Trong ng&amp;agrave;y đặc biệt n&amp;agrave;y, &amp;ocirc;ng Peter tỏ ra đặc biệt khỏe mạnh v&amp;agrave; hạnh ph&amp;uacute;c. &amp;quot;Ch&amp;uacute;ng t&amp;ocirc;i đ&amp;atilde; đi qua biết bao nỗi buồn, sự hoang mang, vất vả. Nhưng h&amp;ocirc;m nay, tất cả chỉ l&amp;agrave; niềm vui&amp;quot;, Lisa chia sẻ.&lt;/p&gt;\r\n\r\n&lt;p&gt;Sau ng&amp;agrave;y cưới, người vợ lập một trang c&amp;aacute; nh&amp;acirc;n ri&amp;ecirc;ng chuy&amp;ecirc;n hướng dẫn c&amp;aacute;ch chăm s&amp;oacute;c người bệnh mắc Alzheimer. B&amp;agrave; chia sẻ những vấn đề m&amp;agrave; bệnh nh&amp;acirc;n v&amp;agrave; người nh&amp;agrave; phải đối mặt như khi bệnh tiến triển như ảo gi&amp;aacute;c, hoang tưởng v&amp;agrave; mất tr&amp;iacute; nhớ ho&amp;agrave;n to&amp;agrave;n.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;&lt;strong&gt;Vy Trang&lt;/strong&gt;&amp;nbsp;(Theo&amp;nbsp;&lt;em&gt;CNN&lt;/em&gt;)&lt;/p&gt;\r\n', 'nguoi-dan-ong-mat-tri-nho-cau-hon-lai-vo-minh', './uploads/images/1624898894_vo-chong-2-3394-1624784199.jpg', 1, 3, '2021-06-28 23:48:14', '2021-06-28 18:24:47'),
(6, 4, 15, 'Doanh nghiệp chạy đua IPO kỷ lục', '&lt;p&gt;C&amp;aacute;c c&amp;ocirc;ng ty đang chạy đua ni&amp;ecirc;m yết cổ phiếu nhiều hơn bao giờ hết v&amp;igrave; họ muốn tận dụng thời gian thị trường chứng kho&amp;aacute;n kh&amp;ocirc;ng ngừng lập kỷ lục mới.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo số liệu thống k&amp;ecirc; bởi&amp;nbsp;&lt;em&gt;Bloomberg&lt;/em&gt;, ước t&amp;iacute;nh c&amp;aacute;c doanh nghiệp tr&amp;ecirc;n to&amp;agrave;n cầu đ&amp;atilde; huy động được lượng vốn kỷ lục gần 350 tỷ USD trong 6 th&amp;aacute;ng đầu năm nay, cao hơn mức đỉnh 282 tỷ USD của nửa cuối năm 2020. C&amp;aacute;c doanh nh&amp;acirc;n v&amp;agrave; ng&amp;acirc;n h&amp;agrave;ng nhờ vậy đ&amp;atilde; kiếm được rất nhiều tiền.&lt;/p&gt;\r\n\r\n&lt;p&gt;Cuộc đua IPO đ&amp;atilde; được khởi động từ năm ngo&amp;aacute;i khi nh&amp;agrave; đầu tư quan t&amp;acirc;m nhiều đến c&amp;aacute;c dịch vụ số, c&amp;aacute;c doanh nghiệp sở hữu c&amp;ocirc;ng nghệ hỗ trợ cho việc người d&amp;acirc;n ở nh&amp;agrave; nhận được nhiều tiền đầu tư. C&amp;ugrave;ng l&amp;uacute;c đ&amp;oacute;, nh&amp;oacute;m c&amp;aacute;c c&amp;ocirc;ng ty được lập ra với mục đ&amp;iacute;ch th&amp;acirc;u t&amp;oacute;m đặc biệt (SPACs) cũng tr&amp;agrave;n ngập thị trường IPO. Sang năm nay, khi gi&amp;aacute; cổ phiếu kh&amp;ocirc;ng ngừng tăng trưởng mạnh, xu thế n&amp;agrave;y đ&amp;atilde; mở rộng ra cả c&amp;aacute;c c&amp;ocirc;ng ty năng lượng t&amp;aacute;i tạo v&amp;agrave; c&amp;ocirc;ng ty b&amp;aacute;n lẻ trực tuyến.&lt;/p&gt;\r\n\r\n&lt;p&gt;Doanh nghiệp trong nhiều ng&amp;agrave;nh nghề kh&amp;aacute;c nhau, từ C&amp;ocirc;ng ty sữa d&amp;ecirc; Thụy Điển Oatly Group AB cho đến C&amp;ocirc;ng ty sản xuất ủng Dr. Martens cũng đều đ&amp;atilde; ch&amp;agrave;o b&amp;aacute;n cổ phiếu trong năm 2021. Tuy nhi&amp;ecirc;n, nh&amp;oacute;m c&amp;aacute;c doanh nghiệp c&amp;ocirc;ng nghệ vẫn chiếm kh&amp;aacute; đ&amp;ocirc;ng trong số c&amp;aacute;c vụ IPO. C&amp;ocirc;ng ty Didi Global sẽ c&amp;oacute; đợt IPO lớn nhất Mỹ trong thập kỷ qua nếu doanh nghiệp vẫn tiếp tục thực thi kế hoạch ch&amp;agrave;o b&amp;aacute;n khoảng 4 tỷ USD cổ phiếu.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng Aaron Arth, trưởng bộ phận t&amp;agrave;i ch&amp;iacute;nh phụ tr&amp;aacute;ch c&amp;aacute;c nước ch&amp;acirc;u &amp;Aacute; (kh&amp;ocirc;ng t&amp;iacute;nh Nhật) tại tổ chức t&amp;agrave;i ch&amp;iacute;nh Goldman Sachs, nhận x&amp;eacute;t: &amp;quot;Thị trường t&amp;agrave;i ch&amp;iacute;nh nhiều nơi, từ New York cho đến Hong Kong, đều tăng trưởng n&amp;oacute;ng trong nửa đầu năm nay v&amp;agrave; thậm ch&amp;iacute; c&amp;ograve;n khiến cho nhiều người nhớ lại bong b&amp;oacute;ng dotcom thập ni&amp;ecirc;n 1990&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hoạt động IPO trở n&amp;ecirc;n s&amp;ocirc;i động nhờ lượng tiền dồi d&amp;agrave;o m&amp;agrave; ng&amp;acirc;n h&amp;agrave;ng trung ương nhiều nước đ&amp;atilde; bơm v&amp;agrave;o nền kinh tế c&amp;ugrave;ng sự trỗi dậy của tầng lớp nh&amp;agrave; đầu tư c&amp;aacute; nh&amp;acirc;n, những người sẵn s&amp;agrave;ng mua một phần của c&amp;ocirc;ng ty m&amp;agrave; họ y&amp;ecirc;u th&amp;iacute;ch.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c ng&amp;acirc;n h&amp;agrave;ng đầu tư tr&amp;ecirc;n thế giới hưởng lợi nhiều, họ thu được nguồn ph&amp;iacute; bảo l&amp;atilde;nh v&amp;agrave; tư vấn lớn. Hai tổ chức t&amp;agrave;i ch&amp;iacute;nh bao gồm Citigroup v&amp;agrave; Goldman Sachs đang dẫn đầu bảng xếp hạng tư vấn v&amp;agrave; bảo l&amp;atilde;nh cho c&amp;aacute;c đợt IPO doanh nghiệp trong năm nay.&lt;/p&gt;\r\n\r\n&lt;p&gt;Khi m&amp;agrave; c&amp;oacute; qu&amp;aacute; nhiều doanh nghiệp đổ x&amp;ocirc; IPO, dường như thị trường c&amp;oacute; dấu hiệu sẽ b&amp;atilde;o h&amp;ograve;a. Nh&amp;agrave; đầu tư khi c&amp;oacute; nhiều lựa chọn sẽ trở n&amp;ecirc;n kh&amp;oacute; t&amp;iacute;nh hơn v&amp;agrave; cũng ngại trả gi&amp;aacute; cao cho cổ phiếu của những c&amp;ocirc;ng ty đang tăng trưởng nhanh vốn ngập tr&amp;agrave;n thị trường IPO.&lt;/p&gt;\r\n\r\n&lt;p&gt;Kết quả, cổ phiếu của nhiều doanh nghiệp c&amp;oacute; hồ sơ đẹp đ&amp;atilde; sụt gi&amp;aacute; mạnh ngay trong phi&amp;ecirc;n ch&amp;agrave;o s&amp;agrave;n, nhiều c&amp;ocirc;ng ty v&amp;igrave; vậy kh&amp;ocirc;ng khỏi thất vọng. Cổ phiếu của c&amp;ocirc;ng ty giao thực phẩm Deliveroo Plc giảm 26% trong ng&amp;agrave;y giao dịch đầu ti&amp;ecirc;n tr&amp;ecirc;n thị trường London; cổ phiếu Oscar Health, c&amp;ocirc;ng ty bảo hiểm đồng s&amp;aacute;ng lập bởi Josh Kushner, đ&amp;atilde; giảm 40% t&amp;iacute;nh từ khi gia nhập thị trường New York.&lt;/p&gt;\r\n\r\n&lt;p&gt;Mới đ&amp;acirc;y, C&amp;ocirc;ng ty Nord Gold Plc của Nga đ&amp;atilde; phải r&amp;uacute;t kế hoạch IPO bởi l&amp;yacute; do thị trường c&amp;oacute; nhiều bất ổn v&amp;agrave; gi&amp;aacute; v&amp;agrave;ng biến động bất thường. Trong th&amp;aacute;ng trước, tổ chức Genworth Financial đ&amp;atilde; tr&amp;igrave; ho&amp;atilde;n việc ch&amp;agrave;o b&amp;aacute;n cổ phiếu của c&amp;ocirc;ng ty th&amp;agrave;nh vi&amp;ecirc;n Enact Holdings. Ngo&amp;agrave;i ra, c&amp;ocirc;ng ty Geely Automobile Holdings đang ni&amp;ecirc;m yết cổ phiếu tại Hong Kong cũng đ&amp;atilde; r&amp;uacute;t hồ sơ ni&amp;ecirc;m yết cổ phiếu tại Thượng Hải.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trưởng bộ phận thị trường vốn khu vực ch&amp;acirc;u &amp;Acirc;u, &amp;ocirc;ng Saadi Soudavar, nhận x&amp;eacute;t: &amp;quot;R&amp;otilde; r&amp;agrave;ng c&amp;oacute; dấu hiệu nh&amp;agrave; đầu tư cảm thấy mệt mỏi v&amp;agrave; ng&amp;agrave;y một k&amp;eacute;n chọn hơn. Đ&amp;acirc;y l&amp;agrave; một năm IPO kỷ lục ch&amp;iacute;nh v&amp;igrave; vậy họ c&amp;oacute; thể c&amp;oacute; lựa chọn của ri&amp;ecirc;ng m&amp;igrave;nh&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nhu cầu của nh&amp;agrave; đầu tư đối với một loại h&amp;igrave;nh ni&amp;ecirc;m yết cổ phiếu hiện giảm đi. C&amp;aacute;c c&amp;ocirc;ng ty SPAC chiếm khoảng một nửa tr&amp;ecirc;n thị trường IPO to&amp;agrave;n cầu trong qu&amp;yacute; 1, tuy nhi&amp;ecirc;n tỷ lệ n&amp;agrave;y sang đến qu&amp;yacute; 2 chỉ c&amp;ograve;n khoảng 13%.&lt;/p&gt;\r\n\r\n&lt;p&gt;Chỉ số theo d&amp;otilde;i c&amp;aacute;c đợt ni&amp;ecirc;m yết cổ phiếu của c&amp;aacute;c c&amp;ocirc;ng ty đ&amp;atilde; giảm khoảng 23% so với mức cao v&amp;agrave;o th&amp;aacute;ng 2 năm nay. T&amp;igrave;nh trạng cổ phiếu ch&amp;agrave;o s&amp;agrave;n suy giảm đ&amp;atilde; kh&amp;ocirc;ng khỏi g&amp;acirc;y tổn hại đến niềm tin của thị trường. Giới chức t&amp;agrave;i ch&amp;iacute;nh đ&amp;atilde; đưa ra nhiều cảnh b&amp;aacute;o với nh&amp;agrave; đầu tư c&amp;aacute; nh&amp;acirc;n cần cẩn trọng với c&amp;aacute;c quyết định của m&amp;igrave;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tuy nhi&amp;ecirc;n, chừng n&amp;agrave;o thị trường chứng kho&amp;aacute;n vẫn tăng điểm, l&amp;agrave;n s&amp;oacute;ng IPO sẽ kh&amp;oacute; m&amp;agrave; dịu đi, tổng gi&amp;aacute; trị c&amp;aacute;c đợt IPO trong năm nay c&amp;oacute; thể vượt kỷ lục 420,1 tỷ USD của năm 2017. Cơn sốt IPO cũng sẽ vẫn tiếp tục trong khoảng từ 6 đến 12 th&amp;aacute;ng tới, theo dự b&amp;aacute;o của đồng trưởng bộ phận thị trường vốn tại tổ chức Jefferies Financial Group &amp;ndash; &amp;ocirc;ng Rob Leach.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;&lt;strong&gt;Diệu Thanh&lt;/strong&gt;&amp;nbsp;&lt;em&gt;(Theo Bloomberg)&lt;/em&gt;&lt;/p&gt;\r\n', 'doanh-nghiep-chay-dua-ipo-ky-luc', './uploads/images/1624899071_uunhuocdiemcuaipo-1624848226-4127-1624848276.jpg', 1, 1, '2021-06-28 23:51:11', '2021-06-28 17:19:08'),
(7, 4, 15, 'Bài toán khi doanh nghiệp tại Bắc Giang khôi phục sản xuất', '&lt;p&gt;Gần 400 c&amp;ocirc;ng ty tại Bắc Giang sẽ sản xuất trở lại từ 1/7 nhưng trước mắt c&amp;aacute;c chủ doanh nghiệp vẫn c&amp;ograve;n nhiều r&amp;agrave;o cản cần th&amp;aacute;o gỡ.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong gần một th&amp;aacute;ng kh&amp;ocirc;i phục hoạt động 4 khu c&amp;ocirc;ng nghiệp Đ&amp;igrave;nh Tr&amp;aacute;m, Quang Ch&amp;acirc;u, V&amp;acirc;n Trung v&amp;agrave; Song Kh&amp;ecirc; - Nội Ho&amp;agrave;ng, 178 doanh nghiệp được chấp thuận hoạt động trở lại với hơn 19.300 lao động. Trong đ&amp;oacute;, 22 doanh nghiệp l&amp;agrave; vendor (nh&amp;agrave; cung cấp) của Samsung với hơn 4.400 lao động đ&amp;atilde; l&amp;agrave;m việc, bằng khoảng 46% số lao động doanh nghiệp đăng k&amp;yacute; sử dụng. Thực tế số lao động ch&amp;iacute;nh thức trở lại l&amp;agrave;m việc &amp;iacute;t hơn, 15-20% trong tổng số 140.000 lao động.&lt;/p&gt;\r\n\r\n&lt;p&gt;Sản xuất lại đ&amp;atilde; gần ba tuần nay, C&amp;ocirc;ng ty Hiraki Việt Nam (Khu c&amp;ocirc;ng nghiệp Đ&amp;igrave;nh Tr&amp;aacute;m, Việt Y&amp;ecirc;n, Bắc Giang) mới đạt 30% số lao động trở lại l&amp;agrave;m việc so với trước khi dịch b&amp;ugrave;ng ph&amp;aacute;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;Để c&amp;oacute; được lao động l&amp;uacute;c n&amp;agrave;y, một phần trong tổng diện t&amp;iacute;ch 10.000 m2 nh&amp;agrave; xưởng được doanh nghiệp n&amp;agrave;y chuyển đổi c&amp;ocirc;ng năng th&amp;agrave;nh chỗ ăn, ở cho c&amp;ocirc;ng nh&amp;acirc;n theo quy định của ch&amp;iacute;nh quyền địa phương. Doanh nghiệp cũng bố tr&amp;iacute; xe đưa đ&amp;oacute;n c&amp;ocirc;ng nh&amp;acirc;n đi l&amp;agrave;m h&amp;agrave;ng ng&amp;agrave;y.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;C&ocirc;ng nh&acirc;n Bắc Giang trở lại nh&agrave; m&aacute;y l&agrave;m việc sau thời gian gi&aacute;n đoạn v&igrave; dịch, cuối th&aacute;ng 5/2021. Ảnh:Nguyễn Ngọc&quot; src=&quot;https://i1-kinhdoanh.vnecdn.net/2021/06/26/cong-nhan-BG-tro-lai-lam-viec-9978-8676-1624724853.jpg?w=680&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=Yz6K4oxN3sOEUhctFbX2TQ&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;ocirc;ng nh&amp;acirc;n Bắc Giang trở lại nh&amp;agrave; m&amp;aacute;y l&amp;agrave;m việc sau thời gian gi&amp;aacute;n đoạn v&amp;igrave; dịch, cuối th&amp;aacute;ng 5/2021. Ảnh:&amp;nbsp;&lt;em&gt;Nguyễn Ngọc.&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Mỗi ng&amp;agrave;y số c&amp;ocirc;ng nh&amp;acirc;n quay trở lại l&amp;agrave;m việc tại nh&amp;agrave; m&amp;aacute;y tăng th&amp;ecirc;m một &amp;iacute;t, giờ mới c&amp;oacute; 60-70 lao động đi l&amp;agrave;m lại&amp;quot;, &amp;ocirc;ng Nguyễn Đức Cường - Tổng gi&amp;aacute;m đốc C&amp;ocirc;ng ty Hiraki Việt Nam n&amp;oacute;i v&amp;agrave; cho biết, doanh nghiệp đang đối diện với thiếu hụt lao động trong qu&amp;aacute; tr&amp;igrave;nh t&amp;aacute;i khởi động sản xuất.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ngay cả với phương &amp;aacute;n tỉnh mới đưa ra gần đ&amp;acirc;y, cho ph&amp;eacute;p doanh nghiệp bố tr&amp;iacute; chỗ ở cho c&amp;ocirc;ng nh&amp;acirc;n theo hướng ở cả trong lẫn ngo&amp;agrave;i khu vực sản xuất, theo &amp;ocirc;ng Cường cũng kh&amp;ocirc;ng dễ d&amp;agrave;ng. Doanh nghiệp tăng chi ph&amp;iacute; khi phải trả tiền thu&amp;ecirc; chỗ ăn, ở cho c&amp;ocirc;ng nh&amp;acirc;n, chi ph&amp;iacute; thu&amp;ecirc; xe đưa đ&amp;oacute;n...&lt;/p&gt;\r\n\r\n&lt;p&gt;Với doanh nghiệp c&amp;oacute; tới 11.000 lao động như Crystal Martin, phương &amp;aacute;n bố tr&amp;iacute; ăn ở tại chỗ hay thu&amp;ecirc; chỗ ở cho c&amp;ocirc;ng nh&amp;acirc;n cũng đều kh&amp;oacute;. &amp;quot;Số lượng lao động đ&amp;ocirc;ng n&amp;ecirc;n doanh nghiệp cũng kh&amp;ocirc;ng thể bố tr&amp;iacute; th&amp;ecirc;m chỗ ăn, ở tại khu k&amp;iacute; t&amp;uacute;c x&amp;aacute;&amp;quot;, đại diện doanh nghiệp n&amp;agrave;y chia sẻ v&amp;agrave; hy vọng Bắc Giang sớm c&amp;oacute; phương &amp;aacute;n th&amp;aacute;o gỡ kh&amp;oacute; khăn, gi&amp;uacute;p doanh nghiệp giảm chi ph&amp;iacute; v&amp;agrave; &amp;quot;h&amp;uacute;t&amp;quot; được lao động trở lại l&amp;agrave;m việc.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo &amp;ocirc;ng Nguyễn Xu&amp;acirc;n Ngọc - Ph&amp;oacute; trưởng Ban quản l&amp;yacute; khu c&amp;ocirc;ng nghiệp Bắc Giang, c&amp;aacute;c doanh nghiệp đều sản xuất theo d&amp;acirc;y chuyền li&amp;ecirc;n ho&amp;agrave;n, thiếu một mắt x&amp;iacute;ch sẽ kh&amp;ocirc;ng ho&amp;agrave;n thiện được sản phẩm. N&amp;ecirc;n trước những n&amp;uacute;t thắt của doanh nghiệp trong thời gian đầu kh&amp;ocirc;i phục lại sản xuất, tỉnh Bắc Giang ng&amp;agrave;y 26/6 quyết định &amp;quot;nới&amp;quot; biện ph&amp;aacute;p ph&amp;ograve;ng dịch theo hướng ph&amp;acirc;n luồng người lao động theo v&amp;ugrave;ng xanh (an to&amp;agrave;n dịch bệnh) v&amp;agrave; v&amp;ugrave;ng v&amp;agrave;ng (nguy cơ l&amp;acirc;y nhiễm thấp) v&amp;agrave; di chuyển đảm bảo &amp;quot;2 điểm, 1 dừng&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Với người lao động đang ở trong &amp;quot;v&amp;ugrave;ng xanh&amp;quot; thuộc tỉnh Bắc Giang, họ được lựa chọn ở tại nơi lưu tr&amp;uacute; của doanh nghiệp hoặc đi l&amp;agrave;m v&amp;agrave; trở về nơi cư tr&amp;uacute;, tạm tr&amp;uacute; h&amp;agrave;ng ng&amp;agrave;y bằng phương tiện c&amp;aacute; nh&amp;acirc;n hoặc xe đưa đ&amp;oacute;n c&amp;ocirc;ng nh&amp;acirc;n của doanh nghiệp. Lao động trước khi quay trở lại l&amp;agrave;m việc một ng&amp;agrave;y phải được x&amp;eacute;t nghiệm s&amp;agrave;ng lọc Covid-19 bằng phương ph&amp;aacute;p RT-PCR.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nếu lao động tự di chuyển bằng phương tiện c&amp;aacute; nh&amp;acirc;n đến nh&amp;agrave; xưởng, phải thực hiện nguy&amp;ecirc;n tắc &amp;quot;2 điểm, 1 đường&amp;quot;. Tức l&amp;agrave;, c&amp;ocirc;ng nh&amp;acirc;n phải cam kết chỉ di chuyển từ nơi cư tr&amp;uacute;, tạm tr&amp;uacute; đến doanh nghiệp v&amp;agrave; ngược lại, kh&amp;ocirc;ng được ph&amp;eacute;p dừng, đỗ, đi v&amp;agrave;o những nơi kh&amp;aacute;c tiếp x&amp;uacute;c với cộng đồng b&amp;ecirc;n ngo&amp;agrave;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Với lao động ở trong &amp;quot;v&amp;ugrave;ng v&amp;agrave;ng&amp;quot;, doanh nghiệp phải bố tr&amp;iacute; nơi lưu tr&amp;uacute; tập trung, phương tiện đưa đ&amp;oacute;n theo nguy&amp;ecirc;n tắc tr&amp;ecirc;n mỗi phương tiện chỉ c&amp;oacute; c&amp;ocirc;ng nh&amp;acirc;n một doanh nghiệp. Xe chở c&amp;ocirc;ng nh&amp;acirc;n kh&amp;ocirc;ng được ph&amp;eacute;p dừng, đỗ, đi v&amp;agrave;o những nơi kh&amp;aacute;c ngo&amp;agrave;i điểm đ&amp;oacute;n, trả.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tỉnh Bắc Giang cũng cho ph&amp;eacute;p doanh nghiệp đ&amp;oacute;n lao động ngo&amp;agrave;i tỉnh, nơi c&amp;oacute; nguy cơ thấp. Tỉnh n&amp;agrave;y đ&amp;atilde; thiết lập c&amp;aacute;c &amp;quot;v&amp;ugrave;ng xanh&amp;quot; d&amp;agrave;nh cho lao động tới từ c&amp;aacute;c tỉnh ngo&amp;agrave;i Bắc Giang. Hoạt động đưa đ&amp;oacute;n c&amp;ocirc;ng nh&amp;acirc;n vẫn phải tu&amp;acirc;n thủ nguy&amp;ecirc;n tắc &amp;quot;2 điểm, 1 đường&amp;quot;; doanh nghiệp phải x&amp;eacute;t nghiệm s&amp;agrave;ng lọc Covid-19 bằng phương ph&amp;aacute;p RT-PCR, c&amp;oacute; kết quả &amp;acirc;m t&amp;iacute;nh mới được v&amp;agrave;o l&amp;agrave;m việc.&lt;/p&gt;\r\n\r\n&lt;p&gt;H&amp;agrave;ng ng&amp;agrave;y Sở Y tế Bắc Giang sẽ c&amp;ocirc;ng bố c&amp;ocirc;ng khai c&amp;aacute;c &amp;quot;v&amp;ugrave;ng xanh&amp;quot; để người d&amp;acirc;n, doanh nghiệp theo d&amp;otilde;i, gi&amp;uacute;p họ chủ động kế hoạch sản xuất, đưa đ&amp;oacute;n c&amp;ocirc;ng nh&amp;acirc;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Với quyết định ph&amp;acirc;n luồng lao động theo v&amp;ugrave;ng xanh - v&amp;agrave;ng, b&amp;agrave;i to&amp;aacute;n kh&amp;oacute; khăn lao động phần n&amp;agrave;o được th&amp;aacute;o gỡ. Tuy vậy, ngo&amp;agrave;i chuyện thiếu hụt lao động sản xuất,&amp;nbsp;&lt;strong&gt;mối lo của nhiều &amp;ocirc;ng chủ doanh nghiệp tại Bắc Giang l&amp;uacute;c n&amp;agrave;y l&amp;agrave; l&amp;agrave;m sao &amp;quot;nối lại&amp;quot; chuỗi cung ứng vốn đ&amp;atilde; bị gi&amp;aacute;n đoạn khi buộc dừng sản xuất do Covid-19.&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Trước khi đợt dịch thứ tư b&amp;ugrave;ng ph&amp;aacute;t v&amp;agrave; Bắc Giang trở th&amp;agrave;nh t&amp;acirc;m dịch, nh&amp;agrave; m&amp;aacute;y rộng hơn 10.000 m2 của Hiraki Việt Nam tại Khu c&amp;ocirc;ng nghiệp Đ&amp;igrave;nh Tr&amp;aacute;m hoạt động hết c&amp;ocirc;ng suất với 400-500 bộ khu&amp;ocirc;n mẫu cho c&amp;aacute;c đối t&amp;aacute;c Canon, Panasonic, Kyoceca... mỗi ng&amp;agrave;y. Nhưng từ khi dịch xảy ra, đối t&amp;aacute;c thu khu&amp;ocirc;n, chuyển nơi kh&amp;aacute;c để tr&amp;aacute;nh đứt đoạn chuỗi sản xuất, cung ứng đơn h&amp;agrave;ng của họ.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng Cường cho rằng, d&amp;ugrave; nh&amp;agrave; m&amp;aacute;y được hoạt động sản xuất ho&amp;agrave;n to&amp;agrave;n trở lại, &amp;iacute;t nhất phải sau 3 th&amp;aacute;ng mới c&amp;oacute; thể kh&amp;ocirc;i phục được trạng th&amp;aacute;i như trước khi c&amp;oacute; dịch.&lt;/p&gt;\r\n\r\n&lt;p&gt;Mặt kh&amp;aacute;c do l&amp;agrave; một nh&amp;agrave; cung ứng trong chuỗi sản xuất, doanh nghiệp như Hiraki chỉ kh&amp;ocirc;i phục ho&amp;agrave;n to&amp;agrave;n doanh số nếu c&amp;aacute;c nh&amp;agrave; cung ứng c&amp;ograve;n lại trở lại sản xuất. Đ&amp;acirc;y cũng l&amp;agrave; đặc th&amp;ugrave; của m&amp;ocirc; h&amp;igrave;nh sản xuất theo chuỗi.&lt;/p&gt;\r\n\r\n&lt;p&gt;Chẳng hạn, ba doanh nghiệp Việt l&amp;agrave; đối t&amp;aacute;c cung ứng, c&amp;ugrave;ng tham gia v&amp;agrave;o chuỗi sản xuất của doanh nghiệp nước ngo&amp;agrave;i với từng kh&amp;acirc;u đoạn sơn mạ, khu&amp;ocirc;n nhựa, ốc v&amp;iacute;t... Giả sử một trong 3 doanh nghiệp n&amp;agrave;y bị dừng sản xuất do dịch, c&amp;aacute;c doanh nghiệp c&amp;ograve;n lại cũng bị ảnh hưởng khi đối t&amp;aacute;c FDI kh&amp;ocirc;ng thể ho&amp;agrave;n thiện được sản phẩm ho&amp;agrave;n chỉnh. Trong khi trước đ&amp;oacute;, để v&amp;agrave;o được chuỗi sản xuất của c&amp;aacute;c đối t&amp;aacute;c nước ngo&amp;agrave;i l&amp;agrave; qu&amp;aacute; tr&amp;igrave;nh gian nan.&lt;/p&gt;\r\n\r\n&lt;p&gt;Dừng 4 khu c&amp;ocirc;ng nghiệp từ đầu th&amp;aacute;ng 5, theo t&amp;iacute;nh to&amp;aacute;n. khiến gi&amp;aacute; trị sản xuất c&amp;ocirc;ng nghiệp của Bắc Giang mất 2.000 tỷ đồng, 140.000 lao động phải nghỉ việc. V&amp;igrave; thế, c&amp;acirc;n đối d&amp;ograve;ng tiền với nhiều doanh nghiệp đang l&amp;agrave; b&amp;agrave;i to&amp;aacute;n kh&amp;oacute; trong bối cảnh vừa chống chọi với Covid-19, vừa đảm bảo sản xuất, c&amp;oacute; doanh thu trả lương người lao động.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c doanh nghiệp đề xuất cơ quan c&amp;oacute; thẩm quyền n&amp;ecirc;n xem x&amp;eacute;t gi&amp;atilde;n, ho&amp;atilde;n chi trả bảo hiểm, c&amp;aacute;c khoản vay, tiền thu&amp;ecirc; đất... tr&amp;aacute;nh chuyện họ bị &amp;quot;g&amp;atilde;y&amp;quot; d&amp;ograve;ng tiền do dừng sản xuất kh&amp;aacute; l&amp;acirc;u. Bởi, số doanh nghiệp nhỏ kh&amp;ocirc;ng c&amp;oacute; t&amp;iacute;ch luỹ, quỹ dự ph&amp;ograve;ng th&amp;igrave; để gắng gượng vượt qua đợt dịch lần n&amp;agrave;y, rất kh&amp;oacute; khăn.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ngo&amp;agrave;i ra, họ cũng mong muốn tăng lượng c&amp;ocirc;ng nh&amp;acirc;n được ti&amp;ecirc;m vaccine Covid-19, khi đ&amp;acirc;y vẫn được coi l&amp;agrave; &amp;quot;ch&amp;igrave;a kho&amp;aacute; chốt chặn để trở lại trạng th&amp;aacute;i b&amp;igrave;nh thường mới&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tại cuộc họp th&amp;aacute;o gỡ kh&amp;oacute; khăn cho doanh nghiệp ng&amp;agrave;y 25/6, &amp;ocirc;ng Phan Thế Tuấn - Ph&amp;oacute; chủ tịch UBND Bắc Giang cho biết, tỉnh tiếp tục ưu ti&amp;ecirc;n ti&amp;ecirc;m vaccine cho c&amp;ocirc;ng nh&amp;acirc;n v&amp;agrave; những người l&amp;agrave;m dịch vụ li&amp;ecirc;n quan. C&amp;aacute;c tổ hỗ trợ doanh nghiệp trong khu, cụm c&amp;ocirc;ng nghiệp đưa ra phương &amp;aacute;n cụ thể &amp;quot;kh&amp;oacute; đến đ&amp;acirc;u gỡ đến đ&amp;oacute;&amp;quot;, nhằm cơ bản đưa doanh nghiệp&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/bac-giang-tinh-kich-ban-de-doanh-nghiep-san-xuat-tro-lai-4297873.html&quot; rel=&quot;dofollow&quot;&gt;trở lại sản xuất nhanh nhất từ 1/7&lt;/a&gt;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tỉnh n&amp;agrave;y đặt mục ti&amp;ecirc;u đưa 30.000 c&amp;ocirc;ng nh&amp;acirc;n trở lại l&amp;agrave;m việc v&amp;agrave;o cuối th&amp;aacute;ng 7 v&amp;agrave; cuối năm đạt 120.000 người tại 4 khu c&amp;ocirc;ng nghiệp.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;&lt;strong&gt;Anh Minh&lt;/strong&gt;&lt;/p&gt;\r\n', 'bai-toan-khi-doanh-nghiep-tai-bac-giang-khoi-phuc-san-xuat', './uploads/images/1624899190_cong-nhan-BG-tro-lai-lam-viec-9978-8676-1624724853.jpg', 1, 1, '2021-06-28 23:53:10', '2021-06-28 17:19:00');
INSERT INTO `posts` (`id`, `user_id`, `topic_id`, `title`, `content`, `slug`, `image_path`, `IsPublished`, `views`, `create_date`, `update_date`) VALUES
(8, 4, 2, 'Phó chủ tịch TP HCM: \"Thành phố vẫn tiếp tục Chỉ thị 10\"', '&lt;p&gt;TP HCM dự kiến vẫn &amp;aacute;p dụng biện ph&amp;aacute;p gi&amp;atilde;n c&amp;aacute;ch x&amp;atilde; hội của Chỉ thị 10 sau 0h ng&amp;agrave;y 29/6, theo Ph&amp;oacute; chủ tịch UBND th&amp;agrave;nh phố Dương Anh Đức.&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&amp;ocirc;ng tin tr&amp;ecirc;n được Ph&amp;oacute; chủ tịch UBND th&amp;agrave;nh phố Dương Anh Đức n&amp;oacute;i với b&amp;aacute;o ch&amp;iacute; b&amp;ecirc;n lề cuộc họp b&amp;aacute;o cung cấp th&amp;ocirc;ng tin về c&amp;ocirc;ng t&amp;aacute;c ph&amp;ograve;ng chống Covid-19 tại TP HCM, chiều 28/6.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Chỉ thị 10 kh&amp;ocirc;ng c&amp;oacute; thời gian kết th&amp;uacute;c n&amp;ecirc;n th&amp;agrave;nh phố vẫn &amp;aacute;p dụng để siết chặt c&amp;aacute;c biện ph&amp;aacute;p ph&amp;ograve;ng chống dịch&amp;quot;, &amp;ocirc;ng Đức n&amp;oacute;i. Từ giữa th&amp;aacute;ng 6, &amp;ocirc;ng Đức được Chủ tịch UBND th&amp;agrave;nh phố Nguyễn Th&amp;agrave;nh Phong giao chỉ đạo tất cả vấn đề li&amp;ecirc;n quan ph&amp;ograve;ng chống Covid-19 ở TP HCM.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;Ph&oacute; chủ tịch UBND th&agrave;nh phố Dương Anh Đức chủ tr&igrave; buổi họp b&aacute;o chiều 28/6. Ảnh: Mạnh T&ugrave;ng.&quot; src=&quot;https://i1-vnexpress.vnecdn.net/2021/06/28/duonganhduc-1845-1624887162.jpg?w=680&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=hMusgMkPvyBbYuyJKU-1gA&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Ph&amp;oacute; chủ tịch UBND th&amp;agrave;nh phố Dương Anh Đức chủ tr&amp;igrave; buổi họp b&amp;aacute;o chiều 28/6. Ảnh:&amp;nbsp;&lt;em&gt;Mạnh T&amp;ugrave;ng.&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Trước đ&amp;oacute;, từ ng&amp;agrave;y 20/6 TP HCM gi&amp;atilde;n c&amp;aacute;ch x&amp;atilde; hội theo&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/tp-hcm-nang-cap-do-chong-dich-4296818.html&quot; rel=&quot;dofollow&quot;&gt;Chỉ thị 10&lt;/a&gt;&amp;nbsp;(của UBND th&amp;agrave;nh phố) c&amp;oacute; một số biện ph&amp;aacute;p ph&amp;ograve;ng chống dịch tương tự nội dung&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/tp-hcm-ha-noi-cach-ly-xa-hoi-den-22-4-4085194.html&quot; rel=&quot;dofollow&quot;&gt;Chỉ thị 16&lt;/a&gt;, trong bối cảnh xuất hiện nhiều ca bệnh v&amp;agrave; ổ dịch chưa r&amp;otilde; nguồn l&amp;acirc;y.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong đ&amp;oacute;, c&amp;aacute;c dịch vụ kinh doanh kh&amp;ocirc;ng thiết yếu, c&amp;aacute;c chợ tự ph&amp;aacute;t phải dừng hoạt động. Người d&amp;acirc;n kh&amp;ocirc;ng tụ tập tr&amp;ecirc;n 3 người tại nơi c&amp;ocirc;ng cộng, ngo&amp;agrave;i phạm vi c&amp;ocirc;ng sở, trường học, bệnh viện v&amp;agrave; tu&amp;acirc;n thủ quy tắc 5K của Bộ Y tế.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ngo&amp;agrave;i ra, TPHCM y&amp;ecirc;u cầu mọi người giữ khoảng c&amp;aacute;ch tối thiểu 1,5 m tại c&amp;aacute;c địa điểm c&amp;ocirc;ng cộng; tăng cường xử l&amp;yacute; nghi&amp;ecirc;m c&amp;aacute;c h&amp;agrave;nh vi vi phạm c&amp;ocirc;ng t&amp;aacute;c ph&amp;ograve;ng, chống Covid-19.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c cơ sở, đơn vị nh&amp;agrave; nước đảm bảo đ&amp;uacute;ng quy định về gi&amp;atilde;n c&amp;aacute;ch trong quy tr&amp;igrave;nh l&amp;agrave;m việc. Doanh nghiệp hạn chế tối đa l&amp;agrave;m việc trực tiếp m&amp;agrave; chuyển sang l&amp;agrave;m trực tuyến, nh&amp;acirc;n vi&amp;ecirc;n chỉ đến trụ sở khi cần thiết.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c đơn vị dừng c&amp;aacute;c hội họp kh&amp;ocirc;ng cần thiết, chỉ tổ chức cuộc họp thực sự quan trọng, tập trung kh&amp;ocirc;ng qu&amp;aacute; 10 người trong một ph&amp;ograve;ng, được ch&amp;iacute;nh quyền địa phương cho ph&amp;eacute;p.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tại cuộc họp Ban chỉ đạo chống Covid-19 của TP HCM h&amp;ocirc;m 25/6, Chủ tịch UBND th&amp;agrave;nh phố Nguyễn Th&amp;agrave;nh Phong cho biết th&amp;agrave;nh phố đ&amp;atilde; trải qua 2 đợt gi&amp;atilde;n c&amp;aacute;ch x&amp;atilde; hội v&amp;agrave; đợt gi&amp;atilde;n c&amp;aacute;ch thứ hai sẽ kết th&amp;uacute;c v&amp;agrave;o ng&amp;agrave;y 30/6.&lt;/p&gt;\r\n\r\n&lt;p&gt;V&amp;igrave; vậy, &amp;ocirc;ng y&amp;ecirc;u cầu c&amp;aacute;c sở ng&amp;agrave;nh, quận huyện phải đ&amp;aacute;nh gi&amp;aacute; lại việc triển khai thực hiện c&amp;aacute;c giải ph&amp;aacute;p của Chỉ thị 10 v&amp;agrave; đề ra những giải ph&amp;aacute;p quyết liệt hơn trong những ng&amp;agrave;y tới.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Từng đơn vị phải đ&amp;aacute;nh gi&amp;aacute; lại, trong đ&amp;oacute; xem x&amp;eacute;t thời gian qua kh&amp;acirc;u kiểm tra thực hiện c&amp;aacute;c quy định ph&amp;ograve;ng dịch của th&amp;agrave;nh phố như thế n&amp;agrave;o? Sau ng&amp;agrave;y 30/6 c&amp;oacute; cần tiếp tục gi&amp;atilde;n c&amp;aacute;ch x&amp;atilde; hội nữa kh&amp;ocirc;ng?&amp;quot;, &amp;ocirc;ng Phong n&amp;oacute;i v&amp;agrave; cho rằng th&amp;agrave;nh phố phải xem x&amp;eacute;t thật kỹ t&amp;igrave;nh h&amp;igrave;nh h&amp;igrave;nh mới c&amp;oacute; thể quyết định.&lt;/p&gt;\r\n\r\n&lt;p&gt;H&amp;ocirc;m nay, TP HCM ghi nhận th&amp;ecirc;m 218 ca Covid-19, trong đ&amp;oacute; 11 ca chưa r&amp;otilde; nguồn l&amp;acirc;y, n&amp;acirc;ng tổng số ca tại th&amp;agrave;nh phố đợt dịch n&amp;agrave;y l&amp;ecirc;n 3.463. Th&amp;agrave;nh phố đang xếp thứ hai về tổng số ca trong đợt dịch thứ 4, chỉ sau Bắc Giang (5.663 ca).&lt;/p&gt;\r\n\r\n&lt;p&gt;Họp Ban chỉ đạo ph&amp;ograve;ng chống Covid-19 của TP HCM s&amp;aacute;ng 28/6, Gi&amp;aacute;m đốc Sở Y tế Nguyễn Tấn Bỉnh cho biết t&amp;igrave;nh h&amp;igrave;nh dịch vẫn diễn biến phức tạp. C&amp;aacute;c ca nhiễm trong cộng đồng c&amp;ograve;n gia tăng qua c&amp;aacute;c trường hợp c&amp;oacute; triệu chứng đến kh&amp;aacute;m ở c&amp;aacute;c cơ sở kh&amp;aacute;m, chữa bệnh (tăng 32-42 ca). Đ&amp;acirc;y l&amp;agrave; những ca chỉ điểm từ đ&amp;oacute; tiến h&amp;agrave;nh truy vết c&amp;aacute;c ổ dịch ở c&amp;aacute;c khu trọ, cơ sở sản xuất, chợ đầu mối...&lt;/p&gt;\r\n\r\n&lt;p&gt;TP HCM đ&amp;atilde; chia c&amp;aacute;c quận huyện th&amp;agrave;nh 3 nh&amp;oacute;m nguy cơ để đưa ra giải ph&amp;aacute;p ph&amp;ograve;ng dịch. Trong đ&amp;oacute;, nh&amp;oacute;m nguy cơ rất cao, gồm: B&amp;igrave;nh T&amp;acirc;n, H&amp;oacute;c M&amp;ocirc;n, B&amp;igrave;nh Ch&amp;aacute;nh, quận 8, T&amp;acirc;n Ph&amp;uacute; v&amp;agrave; một phần của TP Thủ Đức (quận Thủ Đức cũ). Đ&amp;acirc;y l&amp;agrave; những khu vực ghi nhận số ca nhiễm cao v&amp;agrave; tăng nhanh, xuất hiện nhiều ổ dịch tốc độ l&amp;acirc;y nhiễm cao, chưa r&amp;otilde; nguồn.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;&lt;strong&gt;Mạnh T&amp;ugrave;ng - Hữu C&amp;ocirc;ng&lt;/strong&gt;&lt;/p&gt;\r\n', 'pho-chu-tich-tp-hcm-thanh-pho-van-tiep-tuc-chi-thi-10', './uploads/images/1624899487_duonganhduc-1624887035-1367-1624887163.jpg', 1, 2, '2021-06-28 23:58:07', '2021-06-28 17:18:22'),
(9, 4, 3, 'Trẻ em Việt kiều chung tay gây quỹ vaccine cho Việt Nam', '&lt;p&gt;Với những h&amp;agrave;nh động nhỏ b&amp;eacute; nhưng c&amp;oacute; sức lan tỏa lớn, nhiều em nhỏ gốc Việt tr&amp;ecirc;n thế giới đang nỗ lực g&amp;acirc;y quỹ vaccine Covid-19 cho Việt Nam.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Việt Nam cố l&amp;ecirc;n! Con y&amp;ecirc;u Việt Nam&amp;quot; l&amp;agrave; lời gửi gắm của hai chị em Anne-Vy, 5 tuổi, người Mỹ gốc Việt, được chia sẻ tr&amp;ecirc;n page Chung tay v&amp;igrave; Việt Nam - Unite for Vietnam trong chiến dịch g&amp;acirc;y quỹ 10.000 liều vaccine cho qu&amp;ecirc; ngoại.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ngo&amp;agrave;i lời nhắn nhủ dễ thương, Anne-Vy v&amp;agrave; em g&amp;aacute;i một tuổi Maiane sẽ g&amp;oacute;p một tiết mục m&amp;uacute;a trong Talents Show: Sống để y&amp;ecirc;u thương, chương tr&amp;igrave;nh trực tuyến nhằm cổ vũ tinh thần cho người d&amp;acirc;n Việt Nam chống dịch, cũng như hưởng ứng chiến dịch g&amp;acirc;y quỹ vaccine.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;D&amp;ugrave; chỉ mới ph&amp;aacute;t động từ giữa th&amp;aacute;ng 6, &amp;yacute; tưởng chương tr&amp;igrave;nh Talents Show do nh&amp;oacute;m Chung tay v&amp;igrave; Việt nam khởi xướng đ&amp;atilde; lan toả trong khắp c&amp;aacute;c gia đ&amp;igrave;nh Việt đang sinh sống tại Mỹ v&amp;agrave; sang cả ch&amp;acirc;u &amp;Acirc;u&amp;quot;, chị Phạm Hằng, đại diện tại Việt Nam của chương tr&amp;igrave;nh Chung tay v&amp;igrave; Việt Nam, chia sẻ với&amp;nbsp;&lt;em&gt;VnExpress&lt;/em&gt;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Chị Hằng cho biết t&amp;iacute;nh đến h&amp;ocirc;m nay, chương tr&amp;igrave;nh đ&amp;atilde; nhận được 60 tiết mục đăng k&amp;yacute; tham dự với nhiều hạng mục như vẽ tranh, h&amp;aacute;t, nhảy, đ&amp;agrave;n, ảo thuật... Th&amp;ocirc;ng qua chương tr&amp;igrave;nh, c&amp;aacute;c em đều muốn g&amp;oacute;p một phần c&amp;ocirc;ng sức nhỏ b&amp;eacute; để c&amp;ugrave;ng với qu&amp;ecirc; hương Việt Nam đẩy l&amp;ugrave;i đại dịch.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Tấm l&amp;ograve;ng của những em b&amp;eacute; mang d&amp;ograve;ng m&amp;aacute;u Việt cũng l&amp;agrave;m lay động tr&amp;aacute;i tim của người lớn, nhắc nhở ch&amp;uacute;ng ta về truyền thống &amp;quot;c&amp;acirc;y c&amp;oacute; cội, suối c&amp;oacute; nguồn&amp;quot;, chị n&amp;oacute;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Những bức tranh của c&amp;aacute;c em b&amp;eacute; gốc Việt gửi về chương tr&amp;igrave;nh Talents Show sẽ được tổ chức đấu gi&amp;aacute; trực tiếp tr&amp;ecirc;n page của chiến dịch. Nh&amp;agrave; t&amp;agrave;i trợ đưa ra mức gi&amp;aacute; cho sản phẩm muốn mua ở trong mục comment mỗi ảnh sản phẩm. Từ ng&amp;agrave;y 28/6, ban tổ chức sẽ kết nối nh&amp;agrave; t&amp;agrave;i trợ c&amp;oacute; mức gi&amp;aacute; cao nhất với t&amp;aacute;c giả/người đại diện qua Facebook để thực hiện việc chuyển khoản v&amp;agrave; chuyển ph&amp;aacute;t sản phẩm. To&amp;agrave;n bộ số tiền đấu gi&amp;aacute; sẽ được chuyển tới Quỹ Vaccine ph&amp;ograve;ng COVID-19 Việt Nam (VFVC).&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://i1-vnexpress.vnecdn.net/2021/06/25/tranh-chien-dich-vaccine-edite-2280-6072-1624622083.jpg?w=1020&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=eZ8laMoEVgDYpl4BFzGcZA&quot; style=&quot;height:672px; width:500px&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Chiến dịch &amp;quot;10.000 liều vaccine ph&amp;ograve;ng Covid-19 cho Việt Nam&amp;quot; được nh&amp;oacute;m Chung tay v&amp;igrave; Việt Nam c&amp;ugrave;ng h&amp;agrave;ng chục tổ chức người Việt ở Mỹ v&amp;agrave; một số quốc gia kh&amp;aacute;c như Canada ph&amp;aacute;t động từ ng&amp;agrave;y 5/6, kh&amp;ocirc;ng l&amp;acirc;u sau khi ch&amp;iacute;nh phủ th&amp;agrave;nh lập VFVC.&lt;/p&gt;\r\n\r\n&lt;p&gt;Chiến dịch k&amp;eacute;o d&amp;agrave;i gần một th&amp;aacute;ng kh&amp;ocirc;ng chỉ l&amp;agrave; dịp để người Việt ở nước ngo&amp;agrave;i hưởng ứng lời k&amp;ecirc;u gọi hỗ trợ cho quỹ vaccine của ch&amp;iacute;nh phủ, m&amp;agrave; c&amp;ograve;n thể hiện tinh thần đo&amp;agrave;n kết, tương th&amp;acirc;n tương &amp;aacute;i tốt đẹp bấy l&amp;acirc;u nay của người Việt.&lt;/p&gt;\r\n\r\n&lt;p&gt;Chiến dịch đ&amp;atilde; c&amp;oacute; hơn 300 lượt quy&amp;ecirc;n g&amp;oacute;p v&amp;agrave; thu về hơn 26.000 USD. Từng giao dịch v&amp;agrave; khoản tiền ủng hộ đều được cập nhật thường xuy&amp;ecirc;n tr&amp;ecirc;n trang&amp;nbsp;&lt;a href=&quot;http://gofundme.com/f/chungtayvaccines&quot; rel=&quot;nofollow&quot;&gt;Gofundme&lt;/a&gt;&amp;nbsp;của chiến dịch v&amp;agrave; to&amp;agrave;n bộ số tiền sẽ bắt đầu chuyển giao cho VFVC từ ng&amp;agrave;y 1/7, sau khi chiến dịch kết th&amp;uacute;c v&amp;agrave;o ng&amp;agrave;y 30/6.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;T&amp;ocirc;i rất cảm động với tinh thần v&amp;agrave; sự ủng hộ của người Việt tr&amp;ecirc;n to&amp;agrave;n thế giới để g&amp;acirc;y quỹ vắc-xin COVID-19 cho Việt Nam. Trong ho&amp;agrave;n cảnh n&amp;agrave;o, đang sinh sống hay l&amp;agrave;m việc ở đ&amp;acirc;u, người Việt cũng đang thể hiện t&amp;igrave;nh cảm đo&amp;agrave;n kết, y&amp;ecirc;u thương nhau để c&amp;ugrave;ng vượt qua đại dịch&amp;quot;, chị Hằng n&amp;oacute;i.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;&lt;strong&gt;Thanh T&amp;acirc;m&lt;/strong&gt;&lt;/p&gt;\r\n', 'tre-em-viet-kieu-chung-tay-gay-quy-vaccine-cho-viet-nam', './uploads/images/1624900094_tranhchiendichvaccineedited-16-1879-6702-1624622083.jpg', 1, 6, '2021-06-29 00:08:14', '2021-06-28 18:24:03'),
(10, 4, 3, 'Người Việt ở châu Âu dần thoát Covid-19', '&lt;p&gt;Ngọc Anh cảm thấy vui khi cuộc sống ở Cộng h&amp;ograve;a Czech đang dần trở lại b&amp;igrave;nh thường sau thời gian d&amp;agrave;i ảm đạm v&amp;igrave; đại dịch ho&amp;agrave;nh h&amp;agrave;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Nh&amp;igrave;n mọi thứ dần ổn định, mọi người c&amp;oacute; thể kinh doanh trở lại, trẻ em được tới trường thay v&amp;igrave; học từ xa, t&amp;ocirc;i cảm thấy vui hơn nhiều&amp;quot;, chị Ngọc Anh, người Việt sống ở th&amp;agrave;nh phố Hradec Kr&amp;aacute;lov&amp;eacute;, c&amp;aacute;ch thủ đ&amp;ocirc; Prague hơn 100 km, chia sẻ với&amp;nbsp;&lt;em&gt;VnExpress&lt;/em&gt;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Chị Ngọc Anh cho biết cuộc sống ở Czech đang dần trở lại b&amp;igrave;nh thường, khi dịch giảm v&amp;agrave; tỷ lệ ti&amp;ecirc;m chủng ng&amp;agrave;y c&amp;agrave;ng tăng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Số ca nhiễm trung b&amp;igrave;nh của Czech hiện tại l&amp;agrave; 650, chưa bằng 5% so với đỉnh điểm hơn 15.000 ca nhiễm hồi cuối th&amp;aacute;ng 10 năm ngo&amp;aacute;i, theo&amp;nbsp;&lt;em&gt;Reuters&lt;/em&gt;. Quốc gia n&amp;agrave;y đ&amp;atilde; b&amp;aacute;o c&amp;aacute;o hơn 1,6 triệu ca nhiễm v&amp;agrave; hơn 30.000 ca tử vong kể từ khi dịch b&amp;ugrave;ng ph&amp;aacute;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;Czech đ&amp;atilde; bắt đầu nới lỏng c&amp;aacute;c biện ph&amp;aacute;p hạn chế ngăn Covid-19 từ ng&amp;agrave;y 12/4. Theo đ&amp;oacute;, lệnh giới nghi&amp;ecirc;m v&amp;agrave;o ban đ&amp;ecirc;m được dỡ bỏ, sau khi được &amp;aacute;p đặt v&amp;agrave;o đầu th&amp;aacute;ng 3. Trẻ em được trở lại trường, nhưng được y&amp;ecirc;u cầu đeo khẩu trang v&amp;agrave; x&amp;eacute;t nghiệm hai lần một tuần. Cửa h&amp;agrave;ng b&amp;aacute;n quần &amp;aacute;o, chợ n&amp;ocirc;ng sản ngo&amp;agrave;i trời, vườn th&amp;uacute; v&amp;agrave; vườn b&amp;aacute;ch thảo cũng nằm trong danh s&amp;aacute;ch được mở cửa, theo&amp;nbsp;&lt;em&gt;AP&lt;/em&gt;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Hầu như mọi thứ đ&amp;atilde; được mở cửa trở lại. Nh&amp;agrave; h&amp;agrave;ng vẫn &amp;aacute;p dụng quy định b&amp;aacute;n mang về, nhưng một tuần nữa sẽ được mở b&amp;aacute;n b&amp;igrave;nh thường&amp;quot;, chị Anh n&amp;oacute;i.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;Nhiều người đứng đợi trước một cửa h&agrave;ng ở thủ đ&ocirc; Prague, Cộng h&ograve;a Czech ng&agrave;y 10/5. Ảnh: AP.&quot; src=&quot;https://i1-vnexpress.vnecdn.net/2021/05/26/czech-mo-cua-ap-10-edited-7805-1622029612.jpg?w=680&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=PKASvzr90_olyoigp06q3A&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-size:9px&quot;&gt;Nhiều người đứng đợi trước một cửa h&amp;agrave;ng ở thủ đ&amp;ocirc; Prague, Cộng h&amp;ograve;a Czech ng&amp;agrave;y 10/5. Ảnh:&amp;nbsp;&lt;em&gt;AP.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Giống nhiều quốc gia ch&amp;acirc;u &amp;Acirc;u kh&amp;aacute;c, Czech cũng đang đẩy mạnh chiến dịch ti&amp;ecirc;m chủng cho người d&amp;acirc;n. Quốc gia hơn 10,6 triệu d&amp;acirc;n đ&amp;atilde; ti&amp;ecirc;m chủng hơn 4,7 triệu liều vaccine. 32,9% d&amp;acirc;n số đ&amp;atilde; được ti&amp;ecirc;m &amp;iacute;t nhất một liều v&amp;agrave; 11,6% đ&amp;atilde; ho&amp;agrave;n th&amp;agrave;nh chương tr&amp;igrave;nh ti&amp;ecirc;m chủng, theo Our World in Data.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ngọc Anh cho biết gia đ&amp;igrave;nh chị đ&amp;atilde; được ti&amp;ecirc;m đủ hai liều vaccine Pfizer. &amp;quot;Mọi người ti&amp;ecirc;m xong chỉ bị sưng đau nhẹ ở chỗ ti&amp;ecirc;m, nhưng một ng&amp;agrave;y l&amp;agrave; hết&amp;quot;, chị n&amp;oacute;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Chị th&amp;ecirc;m rằng bản th&amp;acirc;n ủng hộ kế hoạch triển khai &amp;quot;hộ chiếu vaccine&amp;quot; của ch&amp;acirc;u &amp;Acirc;u, bởi đ&amp;acirc;y l&amp;agrave; giải ph&amp;aacute;p tốt gi&amp;uacute;p mọi người c&amp;oacute; thể đi lại tự do giữa c&amp;aacute;c nước m&amp;agrave; kh&amp;ocirc;ng cần c&amp;aacute;ch ly.&lt;/p&gt;\r\n\r\n&lt;p&gt;Sống ở thị trấn Woking, hạt Surrey, v&amp;ugrave;ng Đ&amp;ocirc;ng Nam nước Anh, c&amp;aacute;ch thủ đ&amp;ocirc; London khoảng 30 ph&amp;uacute;t đi t&amp;agrave;u, L&amp;ecirc; H&amp;agrave; cũng cảm thấy vui mừng khi Anh n&amp;oacute;i ri&amp;ecirc;ng v&amp;agrave; ch&amp;acirc;u &amp;Acirc;u n&amp;oacute;i chung dần mở cửa.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Cuộc sống chưa b&amp;igrave;nh thường lại ho&amp;agrave;n to&amp;agrave;n, nhưng mọi người ai cũng thấy vui v&amp;igrave; kh&amp;ocirc;ng c&amp;ograve;n sống trong cảnh đ&amp;oacute;ng cửa như trước. T&amp;acirc;m l&amp;yacute; thoải m&amp;aacute;i, kh&amp;ocirc;ng c&amp;ograve;n căng thẳng&amp;quot;, H&amp;agrave; n&amp;oacute;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;H&amp;agrave; cho biết giờ kh&amp;ocirc;ng cảm thấy qu&amp;aacute; lo lắng mỗi khi ra ngo&amp;agrave;i, một phần do Anh vẫn giữ quy định đeo khẩu trang v&amp;agrave; giữ khoảng c&amp;aacute;ch an to&amp;agrave;n, một phần do tỷ lệ ti&amp;ecirc;m chủng ở Anh ng&amp;agrave;y c&amp;agrave;ng cao.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tại quốc gia hơn 66,6 triệu người, 56,1% người d&amp;acirc;n đ&amp;atilde; ti&amp;ecirc;m &amp;iacute;t nhất một liều vaccine v&amp;agrave; 33,7% người ho&amp;agrave;n th&amp;agrave;nh chương tr&amp;igrave;nh ti&amp;ecirc;m chủng, theo số liệu mới nhất của Our World in Data.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;T&amp;ocirc;i dự định ti&amp;ecirc;m vaccine trong hai tuần nữa, nhưng hầu hết người m&amp;agrave; t&amp;ocirc;i tiếp x&amp;uacute;c đều ti&amp;ecirc;m vaccine hết rồi n&amp;ecirc;n cảm thấy kh&amp;aacute; y&amp;ecirc;n t&amp;acirc;m&amp;quot;, c&amp;ocirc; n&amp;oacute;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Anh đ&amp;atilde; trải qua những th&amp;aacute;ng m&amp;ugrave;a đ&amp;ocirc;ng tồi tệ v&amp;igrave; đại dịch, khi h&amp;agrave;ng triệu người d&amp;acirc;n phải sống dưới lệnh phong tỏa v&amp;agrave; sự xuất hiện của c&amp;aacute;c biến chủng dễ l&amp;acirc;y nhiễm. Chỉ ri&amp;ecirc;ng trong th&amp;aacute;ng 1, Anh b&amp;aacute;o c&amp;aacute;o gần 32.000 ca tử vong v&amp;igrave; Covid-19. Với c&amp;aacute;c biện ph&amp;aacute;p phong tỏa mạnh tay v&amp;agrave; nỗ lực triển khai vaccine nhanh, hiệu quả, Anh đ&amp;atilde; thu được &amp;quot;tr&amp;aacute;i ngọt&amp;quot;, khi số ca nhiễm v&amp;agrave; tử vong giảm dần.&lt;/p&gt;\r\n\r\n&lt;p&gt;Anh hiện tại b&amp;aacute;o c&amp;aacute;o hơn 2.400 ca nhiễm mới mỗi ng&amp;agrave;y, bằng 4% so với đợt đỉnh điểm hồi th&amp;aacute;ng 1, trong khi số ca tử vong trung b&amp;igrave;nh l&amp;agrave; 7. Quốc gia n&amp;agrave;y ghi nhận tổng cộng gần 4,47 triệu ca nhiễm v&amp;agrave; hơn 127.700 ca tử vong kể từ khi dịch b&amp;ugrave;ng ph&amp;aacute;t. Theo lịch tr&amp;igrave;nh của Thủ tướng Boris Johnson, Anh dự kiến dỡ bỏ quy định gi&amp;atilde;n c&amp;aacute;ch x&amp;atilde; hội v&amp;agrave;o cuối th&amp;aacute;ng 6.&lt;/p&gt;\r\n\r\n&lt;p&gt;H&amp;agrave; chia sẻ d&amp;ugrave; c&amp;ocirc;ng việc chưa thể tốt lại như trước, c&amp;ocirc; vẫn thấy vui khi được ra ngo&amp;agrave;i v&amp;agrave; đi l&amp;agrave;m trở lại. C&amp;ocirc; thấy bản th&amp;acirc;n vẫn may mắn hơn rất nhiều người bị thất nghiệp hay phải ph&amp;aacute; sản v&amp;igrave; đại dịch.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Thấy m&amp;igrave;nh v&amp;agrave; người th&amp;acirc;n vẫn khỏe mạnh, vượt qua mấy đợt đ&amp;oacute;ng cửa l&amp;agrave; mừng lắm rồi. B&amp;acirc;y giờ ai cũng hy vọng cuộc sống sớm trở lại b&amp;igrave;nh thường, để mọi người được thoải m&amp;aacute;i gặp gỡ bạn b&amp;egrave;, gia đ&amp;igrave;nh, được đi du lịch, cũng như tham gia nhiều hoạt động như trước, m&amp;agrave; kh&amp;ocirc;ng cần l&amp;uacute;c n&amp;agrave;o cũng đeo khẩu trang&amp;quot;, c&amp;ocirc; cho hay.&lt;/p&gt;\r\n\r\n&lt;p&gt;Minh Phương, người Việt ở Paris, cho biết c&amp;ocirc; trở lại Ph&amp;aacute;p v&amp;agrave;o th&amp;aacute;ng 2, thời điểm dịch Covid-19 c&amp;oacute; dấu hiệu b&amp;ugrave;ng ph&amp;aacute;t trở lại.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;D&amp;ugrave; chiến dịch ti&amp;ecirc;m chủng được khởi động từ th&amp;aacute;ng 1, tốc độ c&amp;ograve;n chậm v&amp;agrave; chỉ giới hạn ở người cao tuổi. Đến cuối th&amp;aacute;ng 3, Tổng thống Ph&amp;aacute;p một lần nữa th&amp;ocirc;ng b&amp;aacute;o lệnh phong tỏa cho một số khu vực gồm Paris v&amp;agrave; c&amp;aacute;c v&amp;ugrave;ng ph&amp;iacute;a bắc, trước khi mở rộng dần ra to&amp;agrave;n quốc&amp;quot;, Phương kể.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;ocirc; cho biết lệnh phong tỏa kh&amp;ocirc;ng hạn chế khắt khe c&amp;aacute;c hoạt động đi lại v&amp;igrave; l&amp;yacute; do c&amp;aacute; nh&amp;acirc;n, như mọi người c&amp;oacute; thể đi dạo trong b&amp;aacute;n k&amp;iacute;nh 10km từ nh&amp;agrave; ri&amp;ecirc;ng hoặc tụ tập theo nh&amp;oacute;m nhỏ. Tuy nhi&amp;ecirc;n, việc đ&amp;oacute;ng cửa c&amp;aacute;c trung t&amp;acirc;m thương mại, nh&amp;agrave; h&amp;agrave;ng, rạp chiếu phim v&amp;agrave; nhiều địa điểm kh&amp;aacute;c khiến cuộc sống ở Ph&amp;aacute;p trở n&amp;ecirc;n ảm đạm.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Paris vốn l&amp;agrave; trung t&amp;acirc;m giải tr&amp;iacute; với v&amp;ocirc; số hoạt động văn h&amp;oacute;a nghệ thuật, giờ trở n&amp;ecirc;n đ&amp;igrave;u hiu, qu&amp;aacute;n x&amp;aacute; đ&amp;oacute;ng cửa im l&amp;igrave;m. Bản th&amp;acirc;n t&amp;ocirc;i cũng gặp kh&amp;ocirc;ng &amp;iacute;t kh&amp;oacute; khăn trong c&amp;ocirc;ng việc khi Ph&amp;aacute;p đ&amp;oacute;ng cửa bi&amp;ecirc;n giới với c&amp;aacute;c nước ngo&amp;agrave;i ch&amp;acirc;u &amp;Acirc;u&amp;quot;, c&amp;ocirc; n&amp;oacute;i.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;Qu&aacute;n c&agrave; ph&ecirc; chật k&iacute;n kh&aacute;ch ở Paris ng&agrave;y 19/5. Ảnh: NYTimes.&quot; src=&quot;https://i1-vnexpress.vnecdn.net/2021/05/26/Paris-ngay-19-edited-7028-1622029612.jpg?w=680&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=OotCFtTWBOGZcBA2Q2qQGA&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-size:9px&quot;&gt;Qu&amp;aacute;n c&amp;agrave; ph&amp;ecirc; chật k&amp;iacute;n kh&amp;aacute;ch ở Paris ng&amp;agrave;y 19/5. Ảnh:&amp;nbsp;&lt;em&gt;NYTimes.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Đến cuối th&amp;aacute;ng 4, t&amp;igrave;nh h&amp;igrave;nh dịch ở Ph&amp;aacute;p đ&amp;atilde; c&amp;oacute; dấu hiệu suy giảm, đồng thời chiến dịch triển khai vaccine được đẩy mạnh. Điều n&amp;agrave;y tạo điều kiện cho Ph&amp;aacute;p từng bước mở cửa.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ph&amp;aacute;p hiện tại chỉ ghi nhận trung b&amp;igrave;nh 142 ca nhiễm trong mỗi 100.000 d&amp;acirc;n. Quốc gia hơn 67 triệu d&amp;acirc;n đ&amp;atilde; b&amp;aacute;o c&amp;aacute;o hơn 5,6 triệu ca nhiễm v&amp;agrave; hơn 108.000 ca tử vong. Hơn 34,2% d&amp;acirc;n số đ&amp;atilde; ti&amp;ecirc;m &amp;iacute;t nhất một liều vaccine, trong khi 14,7% ho&amp;agrave;n th&amp;agrave;nh chương tr&amp;igrave;nh ti&amp;ecirc;m chủng, theo Our World in Data.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tia s&amp;aacute;ng về cuộc sống b&amp;igrave;nh thường trở lại đến với người d&amp;acirc;n Paris, khi họ được ph&amp;eacute;p d&amp;ugrave;ng c&amp;agrave; ph&amp;ecirc; tại c&amp;aacute;c qu&amp;aacute;n ngo&amp;agrave;i trời, hay tới thăm c&amp;aacute;c viện bảo t&amp;agrave;ng từ ng&amp;agrave;y 19/5 sau 6 th&amp;aacute;ng gi&amp;aacute;n đoạn v&amp;igrave; Covid-19.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Ng&amp;agrave;y 19/5, người d&amp;acirc;n Paris đổ ra đường, xếp h&amp;agrave;ng d&amp;agrave;i b&amp;ecirc;n ngo&amp;agrave;i c&amp;aacute;c cửa tiệm, ngồi k&amp;iacute;n c&amp;aacute;c qu&amp;aacute;n bar ngo&amp;agrave;i trời bất chấp thời tiết &amp;acirc;m u mưa gi&amp;oacute;. Bạn b&amp;egrave; t&amp;ocirc;i đều rủ nhau đi xem phim, c&amp;oacute; người khoe đi v&amp;agrave;i ba lần trong mấy ng&amp;agrave;y chỉ để thỏa niềm mong mỏi bấy l&amp;acirc;u&amp;quot;, Phương kể.&lt;/p&gt;\r\n\r\n&lt;p&gt;Minh Phương tin kế hoạch &amp;aacute;p dụng hộ chiếu vaccine trong thời gian tới ở ch&amp;acirc;u &amp;Acirc;u sẽ khuyến kh&amp;iacute;ch mọi người ti&amp;ecirc;m vaccine. C&amp;ocirc; cũng dự định ti&amp;ecirc;m vaccine v&amp;agrave;o th&amp;aacute;ng 6 tới, sau khi trở về từ chuyến c&amp;ocirc;ng t&amp;aacute;c ở Tunisia.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;&lt;strong&gt;Thanh T&amp;acirc;m&lt;/strong&gt;&lt;/p&gt;\r\n', 'nguoi-viet-o-chau-u-dan-thoat-covid-19', './uploads/images/1624900471_Paris-ngay-19-edited-7028-1622029612.jpg', 1, 0, '2021-06-29 00:14:31', '2021-06-28 17:14:31'),
(11, 4, 3, 'Người gốc Việt tranh cử tổng thư ký bang Georgia', '&lt;p&gt;Bee Nguyen, th&amp;agrave;nh vi&amp;ecirc;n đảng D&amp;acirc;n chủ, tuy&amp;ecirc;n bố cạnh tranh với Brad Raffensperger, Tổng thư k&amp;yacute; bang Georgia đương nhiệm.&lt;/p&gt;\r\n\r\n&lt;p&gt;Cuộc đua v&amp;agrave;o ghế tổng thư k&amp;yacute; bang Georgia trở n&amp;ecirc;n gay cấn v&amp;agrave;o s&amp;aacute;ng 4/5, khi Bee Nguyen, 39 tuổi, người Mỹ gốc Việt đầu ti&amp;ecirc;n được bầu v&amp;agrave;o Hạ viện Georgia, tuy&amp;ecirc;n bố gia nhập.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Đảng Cộng h&amp;ograve;a đ&amp;atilde; sử dụng quyền h&amp;agrave;nh để bịt miệng tiếng n&amp;oacute;i của cử tri, những người lựa chọn một người Mỹ phụng sự v&amp;igrave; tất cả ch&amp;uacute;ng ta, chứ kh&amp;ocirc;ng v&amp;igrave; v&amp;agrave;i người trong ch&amp;uacute;ng ta&amp;quot;, Bee Nguyen n&amp;oacute;i trong video th&amp;ocirc;ng b&amp;aacute;o tranh cử. &amp;quot;Nhưng ch&amp;uacute;ng ta sẽ kh&amp;ocirc;ng cho ph&amp;eacute;p bất kỳ ai cản trở quyền được tiến tới con đường d&amp;acirc;n chủ tự do v&amp;agrave; c&amp;ocirc;ng bằng&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;Bee Nguyen&quot; src=&quot;https://i1-vnexpress.vnecdn.net/2021/05/05/f0f9061e61eb993cd96cbb8fd6b45e-6414-5165-1620189017.jpg?w=680&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=fQ-V-AUyFsaWAr50jaFrKA&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-size:10px&quot;&gt;Bee Nguyen trong một cuộc tuần h&amp;agrave;nh phản đối th&amp;ugrave; gh&amp;eacute;t người gốc &amp;Aacute; tại Atlanta hồi th&amp;aacute;ng 3. Ảnh:&amp;nbsp;&lt;em&gt;Reuters&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Raffensperger, th&amp;agrave;nh vi&amp;ecirc;n đảng Cộng h&amp;ograve;a từng khiến cựu tổng thống Mỹ Donald Trump tức giận v&amp;igrave; từ chối lật ngược kết quả bầu cử, đang đối mặt với th&amp;aacute;ch thức lớn từ ch&amp;iacute;nh th&amp;agrave;nh vi&amp;ecirc;n c&amp;ugrave;ng đảng, nghị sĩ Jody Hice.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong một cuộc phỏng vấn tuần n&amp;agrave;y, Nguyen nhận định Raffensperger xứng đ&amp;aacute;ng được ghi nhận v&amp;igrave; đ&amp;atilde; chống lại Trump, b&amp;aacute;c bỏ tuy&amp;ecirc;n bố của cựu tổng thống về h&amp;agrave;nh vi gian lận cử tri sau cuộc bầu cử th&amp;aacute;ng 11 năm ngo&amp;aacute;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nhưng b&amp;agrave; cũng lưu &amp;yacute; rằng kể từ đ&amp;oacute;, Raffensperger đ&amp;atilde; ủng hộ luật về quyền bỏ phiếu th&amp;ocirc;ng qua hồi th&amp;aacute;ng 3, cũng như tiếp tục ủng hộ Trump sau khi cựu tổng thống đưa ra c&amp;aacute;o buộc v&amp;ocirc; căn cứ về bầu cử ở Georgia.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;T&amp;ocirc;i đ&amp;atilde; đi đầu trong cuộc chiến chống lại luật đ&amp;agrave;n &amp;aacute;p cử tri ở Georgia&amp;quot;, Nguyen n&amp;oacute;i. &amp;quot;Chứng kiến nền d&amp;acirc;n chủ bị x&amp;oacute;i m&amp;ograve;n năm 2020, t&amp;ocirc;i nhận ra tầm quan trọng của việc phải bảo vệ quyền bầu cử của ch&amp;uacute;ng ta&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;T&amp;ocirc;i tin rằng người d&amp;acirc;n Georgia xứng đ&amp;aacute;ng c&amp;oacute; quyền lợi tốt hơn v&amp;agrave; l&amp;agrave;m được tốt hơn&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trump thua 12.000 phiếu ở Georgia. Sau cuộc bầu cử, &amp;ocirc;ng y&amp;ecirc;u cầu cả Tổng thư k&amp;yacute; Raffensperger v&amp;agrave; Thống đốc Brian Kemp, hai th&amp;agrave;nh vi&amp;ecirc;n đảng Cộng h&amp;ograve;a, can thiệp để lật ngược kết quả bầu cử nhưng bị từ chối.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguyen, một người ủng hộ quyền ph&amp;aacute; thai v&amp;agrave; chỉ tr&amp;iacute;ch luật quản l&amp;yacute; s&amp;uacute;ng đạn &amp;quot;lỏng lẻo&amp;quot; của Georgia, c&amp;oacute; thể kết nối với nhiều cử tri bảo thủ hơn ngo&amp;agrave;i khu vực của m&amp;igrave;nh ở Atlanta. B&amp;agrave; l&amp;agrave; th&amp;agrave;nh vi&amp;ecirc;n đảng D&amp;acirc;n chủ, được bầu v&amp;agrave;o hạ viện Georgia trong một cuộc bầu cử đặc biệt năm 2017 để lấp đầy ghế trống sau khi Stacey Abrams từ chức để tranh cử thống đốc.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hồi th&amp;aacute;ng 3, Nguyen nằm trong nh&amp;oacute;m c&amp;aacute;c nh&amp;agrave; lập ph&amp;aacute;p người Mỹ gốc &amp;Aacute; của Georgia đ&amp;atilde; l&amp;ecirc;n &amp;aacute;n mạnh mẽ vụ xả s&amp;uacute;ng tại c&amp;aacute;c tiệm m&amp;aacute;t xa ở Atlanta khiến 8 người thiệt mạng, bao gồm 6 phụ nữ gốc &amp;Aacute;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Cuộc đua v&amp;agrave;o ghế tổng thư k&amp;yacute; Georgia trước đ&amp;acirc;y &amp;iacute;t được ch&amp;uacute; &amp;yacute;, nhưng b&amp;acirc;y giờ n&amp;oacute; được theo d&amp;otilde;i chặt chẽ bởi tỷ lệ ch&amp;ecirc;nh lệch phiếu bầu ở bang n&amp;agrave;y trong những cuộc bầu cử gần đ&amp;acirc;y rất s&amp;iacute;t sao, khiến n&amp;oacute; gần như trở th&amp;agrave;nh một bang chiến trường quan trọng trong cuộc bầu cử tổng thống.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nhiệm vụ chủ yếu của tổng thư k&amp;yacute; bang l&amp;agrave; phụ tr&amp;aacute;ch to&amp;agrave;n bộ c&amp;ocirc;ng t&amp;aacute;c bầu cử của bang như thực thi c&amp;aacute;c quy định bầu cử, gi&amp;aacute;m s&amp;aacute;t bầu cử, đăng k&amp;yacute; cử tri, kiểm phiếu. Ngo&amp;agrave;i ra, tổng thư k&amp;yacute; bang c&amp;ograve;n chịu tr&amp;aacute;ch nhiệm quản l&amp;yacute; c&amp;ocirc;ng chứng vi&amp;ecirc;n, lữu trữ hồ sơ h&amp;agrave;nh ch&amp;iacute;nh.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;&lt;strong&gt;Hồng Hạnh&amp;nbsp;&lt;/strong&gt;(Theo&amp;nbsp;&lt;em&gt;New York Times&lt;/em&gt;)&lt;/p&gt;\r\n', 'nguoi-goc-viet-tranh-cu-tong-thu-ky-bang-georgia', './uploads/images/1624902933_f0f9061e61eb993cd96cbb8fd6b45e-6414-5165-1620189017.jpg', 1, 1, '2021-06-29 00:55:33', '2021-06-28 18:23:37'),
(12, 1, 20, 'Harrison Ford bị thương khi quay \"Indiana Jones 5\"', '&lt;p&gt;T&amp;agrave;i tử Harrison Ford gặp chấn thương ở vai tr&amp;ecirc;n phim trường &amp;quot;Indiana Jones 5&amp;quot; v&amp;agrave; sẽ phải nghỉ ngơi một thời gian.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ng&amp;agrave;y 23/6, nguồn tin của&amp;nbsp;&lt;em&gt;Variety&lt;/em&gt;&amp;nbsp;cho biết: &amp;quot;Trong một buổi tập cảnh chiến đấu, Harrison Ford d&amp;iacute;nh chấn thương ở vai. C&amp;ocirc;ng việc sản xuất phim vẫn diễn ra song song qu&amp;aacute; tr&amp;igrave;nh điều trị. Trong v&amp;agrave;i tuần tới, &amp;ecirc;-k&amp;iacute;p sẽ điều chỉnh lịch quay để thực hiện c&amp;aacute;c cảnh kh&amp;ocirc;ng c&amp;oacute; Ford tham gia trước&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;Harrison Ford trong vai Indiana Jones&quot; src=&quot;https://i1-giaitri.vnecdn.net/2021/06/24/Indiana-Jones-4165-1624529188.jpg?w=680&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=xu2ALS-0ro_p8bl8IvmPNQ&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-size:10px&quot;&gt;Harrison Ford trong vai Indiana Jones. Ảnh:&amp;nbsp;&lt;em&gt;Disney&lt;/em&gt;.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Đo&amp;agrave;n phim kh&amp;ocirc;ng tiết lộ mức độ nghi&amp;ecirc;m trọng của vết thương. Tuy nhi&amp;ecirc;n, Ford dự kiến phải nghỉ v&amp;agrave;i tuần. Nam diễn vi&amp;ecirc;n nhiều lần gặp tai nạn trong sự nghiệp đ&amp;oacute;ng phim h&amp;agrave;nh động. Đầu thập ni&amp;ecirc;n 1980, Ford bị thương nặng ở lưng khi quay&amp;nbsp;&lt;em&gt;Indiana Jones and The Temple of Doom&lt;/em&gt;. &amp;Ocirc;ng cũng bị chấn thương ch&amp;acirc;n khi đ&amp;oacute;ng&lt;em&gt;&amp;nbsp;Star Wars: The Force Awakens&lt;/em&gt;&amp;nbsp;(2015).&lt;/p&gt;\r\n\r\n&lt;p&gt;Dự &amp;aacute;n&amp;nbsp;&lt;em&gt;Indiana Jones 5&lt;/em&gt;&amp;nbsp;bắt đầu bấm m&amp;aacute;y từ tại Anh hồi th&amp;aacute;ng 6. T&amp;agrave;i tử 78 tuổi Harrison Ford tiếp tục giữ vai nh&amp;agrave; khảo cổ đam m&amp;ecirc; phi&amp;ecirc;u lưu. Hai ng&amp;ocirc;i sao Phoebe Waller-Bridge v&amp;agrave; Mads Mikkelsen cũng x&amp;aacute;c nhận tham gia. Đạo diễn Steven Spielberg - người chỉ đạo bốn phần phim trước - xin r&amp;uacute;t khỏi dự &amp;aacute;n v&amp;agrave; nhường vị tr&amp;iacute; cho James Mangold (&lt;em&gt;Logan, Ford v Ferrari&lt;/em&gt;...). Trong năm qua, bộ phim nhiều lần bị tr&amp;igrave; ho&amp;atilde;n lịch sản xuất v&amp;igrave; dịch, dự kiến ho&amp;agrave;n th&amp;agrave;nh v&amp;agrave; ra rạp v&amp;agrave;o th&amp;aacute;ng 7/2022.&lt;/p&gt;\r\n\r\n&lt;p&gt;Harrison Ford sinh năm 1942, được biết đến qua c&amp;aacute;c phim&amp;nbsp;&lt;em&gt;&lt;a href=&quot;https://vnexpress.net/harrison-ford-tang-tiu-voi-carrie-fisher-khi-dong-star-wars-3499743.html&quot; rel=&quot;dofollow&quot;&gt;Star Wars&lt;/a&gt;,&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/harrison-ford-tai-xuat-lam-nguoi-hung-indiana-jones-o-tuoi-73-3370735.html&quot; rel=&quot;dofollow&quot;&gt;Indiana Jones&lt;/a&gt;, Blade Runner&lt;/em&gt;hay&lt;em&gt;Air Force One&lt;/em&gt;... Tổng doanh thu c&amp;aacute;c t&amp;aacute;c phẩm của &amp;ocirc;ng tại ph&amp;ograve;ng v&amp;eacute; đạt hơn 5,1 tỷ USD. Ngo&amp;agrave;i đ&amp;oacute;ng phim, t&amp;agrave;i tử c&amp;oacute; nhiều sở th&amp;iacute;ch mạo hiểm như tự l&amp;aacute;i m&amp;aacute;y bay. Trong qu&amp;aacute; khứ, &amp;ocirc;ng từng v&amp;agrave;i lần tho&amp;aacute;t chết sau khi gặp tai nạn h&amp;agrave;ng kh&amp;ocirc;ng.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;&lt;strong&gt;Phương Mai&lt;/strong&gt;&amp;nbsp;(theo&amp;nbsp;&lt;em&gt;Variety&lt;/em&gt;)&lt;/p&gt;\r\n', 'harrison-ford-bi-thuong-khi-quay-indiana-jones-5', './uploads/images/1624904016_Indiana-Jones-4165-1624529188.jpg', 1, 1, '2021-06-29 01:13:36', '2021-06-28 18:23:32'),
(13, 1, 25, 'Diễn viên \"Ròm\" đoạt giải ở Liên hoan phim châu Á', '&lt;p&gt;Trần Anh Khoa - diễn vi&amp;ecirc;n ch&amp;iacute;nh phim &amp;quot;R&amp;ograve;m&amp;quot; - đoạt giải &amp;quot;Nam diễn vi&amp;ecirc;n xuất sắc&amp;quot; ở Li&amp;ecirc;n hoan phim ch&amp;acirc;u &amp;Aacute;, h&amp;ocirc;m 24/6.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tr&amp;ecirc;n fanpage&lt;em&gt;&amp;nbsp;Asian Film Festival -&amp;nbsp;&lt;/em&gt;diễn ra tại Rome, Italy từ ng&amp;agrave;y 17 đến 23/6, ban gi&amp;aacute;m khảo&amp;nbsp;đ&amp;aacute;nh gi&amp;aacute; diễn xuất của Anh Khoa gi&amp;agrave;u năng lượng, đầy bản năng khi v&amp;agrave;o vai một nh&amp;acirc;n vật tập th&amp;iacute;ch nghi với cuộc sống hỗn loạn giữa l&amp;ograve;ng S&amp;agrave;i G&amp;ograve;n. Do đang du học ở Canada, diễn vi&amp;ecirc;n quay video n&amp;oacute;i cảm nhận l&amp;ecirc;n sự qu&amp;yacute; gi&amp;aacute;, tr&amp;acirc;n trọng khi nhận được giải thưởng đầu ti&amp;ecirc;n trong nghề. Anh cảm ơn anh ruột - đạo diễn&amp;nbsp;&lt;em&gt;R&amp;ograve;m&amp;nbsp;&lt;/em&gt;Trần Thanh Huy - đ&amp;atilde; cho cơ hội đến với nghề diễn, c&amp;oacute; những trải nghiệm độc đ&amp;aacute;o c&amp;ugrave;ng đo&amp;agrave;n l&amp;agrave;m phim.&lt;/p&gt;\r\n\r\n&lt;p&gt;Đạo diễn Trần Thanh Huy x&amp;uacute;c động trước giải quốc tế thứ ba&amp;nbsp;&lt;em&gt;R&amp;ograve;m&amp;nbsp;&lt;/em&gt;nhận được, sau&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/phim-viet-doat-giai-cao-nhat-o-lhp-busan-3995851.html&quot; rel=&quot;dofollow&quot;&gt;giải cao nhất&lt;/a&gt;&amp;nbsp;ở Li&amp;ecirc;n hoan phim quốc tế Busan (2019) v&amp;agrave;&amp;nbsp;&lt;em&gt;Phim đầu tay xuất sắc&lt;/em&gt;&amp;nbsp;ở Li&amp;ecirc;n hoan phim quốc tế Fantasia (2020). Anh mời Khoa đ&amp;oacute;ng&amp;nbsp;&lt;em&gt;R&amp;ograve;m&lt;/em&gt;&amp;nbsp;sau khi thấy diễn xuất của em trai trong phim ngắn&amp;nbsp;&lt;em&gt;16:30.&amp;nbsp;&lt;/em&gt;Đạo diễn cho biết&lt;em&gt;&amp;nbsp;&lt;/em&gt;t&amp;aacute;m năm quay phim,&lt;em&gt;&amp;nbsp;&lt;/em&gt;Khoa thực hiện v&amp;ocirc; số cảnh kh&amp;oacute;.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;Anh Khoa (phải) - vai ch&iacute;nh phim R&ograve;m b&ecirc;n diễn vi&ecirc;n phụ Anh T&uacute; Wilson. Ảnh: CJ.&quot; src=&quot;https://i1-giaitri.vnecdn.net/2021/06/25/rom-2-jpeg-4855-1624588240.jpg?w=680&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=67D5J_KQ1vVz0uwxAokXDg&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-size:10px&quot;&gt;Anh Khoa (phải) - vai ch&amp;iacute;nh phim &amp;quot;R&amp;ograve;m&amp;quot; b&amp;ecirc;n diễn vi&amp;ecirc;n phụ Anh T&amp;uacute; Wilson. Anh Khoa sinh năm 1998 ở TP HCM, đ&amp;oacute;ng phim từ l&amp;uacute;c 14 tuổi, hiện theo học ng&amp;agrave;nh quay phim. Ảnh:&amp;nbsp;&lt;em&gt;CJ.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Chẳng hạn, cảnh R&amp;ograve;m bị rớt xuống hầm mộ s&amp;acirc;u ba m&amp;eacute;t, diễn vi&amp;ecirc;n phải tự leo l&amp;ecirc;n, &amp;ecirc;-k&amp;iacute;p quay hơn 40 lần. Trong cảnh R&amp;ograve;m bức x&amp;uacute;c v&amp;igrave; bị lừa, khi diễn, Khoa t&amp;eacute; từ tr&amp;ecirc;n m&amp;aacute;i nh&amp;agrave; xuống đất v&amp;igrave; lỗi của bộ phận thiết kế. Đạo diễn n&amp;oacute;i: &amp;quot;Sau tất cả, t&amp;ocirc;i muốn gửi lời cảm ơn Khoa đ&amp;atilde; hy sinh v&amp;igrave; phim. Nhưng t&amp;ocirc;i cũng tự tin rằng đ&amp;atilde; cho Khoa một trải nghiệm kh&amp;ocirc;ng phải ai cũng c&amp;oacute; ở thời ni&amp;ecirc;n thiếu&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Phim&amp;nbsp;&lt;em&gt;R&amp;ograve;m&amp;nbsp;&lt;/em&gt;ra rạp hồi th&amp;aacute;ng 9/2020, doanh thu hơn 60 tỷ đồng. Phim&lt;em&gt;&amp;nbsp;&lt;/em&gt;kể về nh&amp;acirc;n vật ch&amp;iacute;nh c&amp;ugrave;ng t&amp;ecirc;n (Trần Anh Khoa đ&amp;oacute;ng) - cậu b&amp;eacute; chuy&amp;ecirc;n l&amp;agrave;m nghề tư vấn số đề cho người d&amp;acirc;n một khu chung cũ. Nếu tr&amp;uacute;ng, cậu được thưởng, sai sẽ bị đ&amp;aacute;nh. C&amp;ugrave;ng nghề với R&amp;ograve;m l&amp;agrave; Ph&amp;uacute;c (Anh T&amp;uacute; Wilson) - một thanh ni&amp;ecirc;n xảo tr&amp;aacute;, th&amp;iacute;ch chơi bẩn. Ph&amp;uacute;c đổ tội cho R&amp;ograve;m tư vấn sai số đề cho một người trong chung cư, khiến cậu bị đuổi đ&amp;aacute;nh, phải nương nhờ nh&amp;agrave; b&amp;agrave; Ghi (C&amp;aacute;t Phượng). R&amp;ograve;m tiếp tục chỉ &amp;ocirc;ng Khắc (Mai Trần) đ&amp;aacute;nh đề v&amp;agrave; tr&amp;uacute;ng. Từ đ&amp;acirc;y, nhiều bi kịch xảy ra.&lt;/p&gt;\r\n\r\n&lt;p&gt;Năm 2019, phim chiến thắng ở hạng mục New Currents ở Li&amp;ecirc;n hoan phim Quốc tế Busan (H&amp;agrave;n Quốc). Tuy vậy, t&amp;aacute;c phẩm bị phạt&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/phim-rom-bi-phat-40-trieu-dong-3996760.html&quot; rel=&quot;dofollow&quot;&gt;40 triệu đồng&lt;/a&gt;&amp;nbsp;v&amp;igrave; tham gia li&amp;ecirc;n hoan phim quốc tế khi chưa được cấp ph&amp;eacute;p ph&amp;aacute;t h&amp;agrave;nh trong nước. Từ th&amp;aacute;ng 3, phim nhận giấy ph&amp;eacute;p&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/phim-rom-co-giay-phep-phat-hanh-4078263.html&quot; rel=&quot;dofollow&quot;&gt;ph&amp;aacute;t h&amp;agrave;nh&lt;/a&gt;&amp;nbsp;của hội đồng duyệt, bị ho&amp;atilde;n ra rạp một lần v&amp;igrave; dịch.&lt;/p&gt;\r\n\r\n&lt;p&gt;Li&amp;ecirc;n hoan phim ch&amp;acirc;u &amp;Aacute; ra đời từ năm 2003, do Quỹ điện ảnh Robert Bresson tổ chức, nhằm giới thiệu c&amp;aacute;c t&amp;aacute;c phẩm điện ảnh ti&amp;ecirc;u biểu của 13 quốc gia ch&amp;acirc;u &amp;Aacute; v&amp;agrave; v&amp;ugrave;ng l&amp;atilde;nh thổ như: Trung Quốc, Nhật Bản, Th&amp;aacute;i Lan, Singapore, Malaysia, Indonesia, Việt Nam, Philippines, H&amp;agrave;n Quốc... C&amp;aacute;c hạng mục ch&amp;iacute;nh được trao cho phim, đạo diễn, nam - nữ diễn vi&amp;ecirc;n ch&amp;iacute;nh xuất sắc. Năm nay, ngo&amp;agrave;i R&amp;ograve;m, một số phim Việt được chiếu ở li&amp;ecirc;n hoan phim như&amp;nbsp;&lt;em&gt;Bắc kim thang&lt;/em&gt;&amp;nbsp;(t&amp;ecirc;n tiếng Anh:&amp;nbsp;&lt;em&gt;Home sweet home&lt;/em&gt;),&lt;em&gt;Tiệc trăng m&amp;aacute;u&amp;nbsp;&lt;/em&gt;(Blood moon party),&amp;nbsp;&lt;em&gt;Bố gi&amp;agrave;&lt;/em&gt;&amp;nbsp;(Dad, I&amp;rsquo;m sorry)...&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;&lt;strong&gt;Tam Kỳ&lt;/strong&gt;&lt;/p&gt;\r\n', 'dien-vien-rom-doat-giai-o-lien-hoan-phim-chau', './uploads/images/1624904218_dienvienrom-1624588072-5913-1624588240.jpg', 1, 0, '2021-06-29 01:16:58', '2021-06-28 18:16:58');
INSERT INTO `posts` (`id`, `user_id`, `topic_id`, `title`, `content`, `slug`, `image_path`, `IsPublished`, `views`, `create_date`, `update_date`) VALUES
(14, 1, 21, 'Những ca khúc Âu Mỹ nổi bật nửa đầu năm', '&lt;p&gt;&amp;quot;Drivers License&amp;quot;, &amp;quot;Butter&amp;quot; v&amp;agrave; &amp;quot;Leave the Door Open&amp;quot; l&amp;agrave; những ca kh&amp;uacute;c &amp;Acirc;u Mỹ nổi bật nửa đầu năm nay.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Drivers License - Olivia Rodrigo&lt;/strong&gt;&lt;br /&gt;\r\nNg&amp;agrave;y ph&amp;aacute;t h&amp;agrave;nh: 8/1&lt;br /&gt;\r\nThể loại: Pop ballad, power ballad&lt;/p&gt;\r\n\r\n&lt;p&gt;Đứng đầu bảng xếp hạng Billboard Hot 100 trong t&amp;aacute;m tuần liền,&amp;nbsp;&lt;em&gt;&lt;a href=&quot;https://vnexpress.net/giai-tri/ca-si-17-tuoi-pha-ky-luc-nhac-truc-tuyen-4223144.html&quot; rel=&quot;dofollow&quot;&gt;Drivers License&lt;/a&gt;&lt;/em&gt;&amp;nbsp;hiện l&amp;agrave; ca kh&amp;uacute;c th&amp;agrave;nh c&amp;ocirc;ng nhất năm. Bản pop ballad kể lại cảm x&amp;uacute;c thất t&amp;igrave;nh của một c&amp;ocirc; g&amp;aacute;i tuổi mới lớn khi bạn trai bỏ đi theo người kh&amp;aacute;c. Ca kh&amp;uacute;c đầu tay của nữ diễn vi&amp;ecirc;n&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/giai-tri/olivia-rodrigo-hien-tuong-am-nhac-17-tuoi-4220517.html&quot; rel=&quot;dofollow&quot;&gt;Olivia Rodrigo&lt;/a&gt;&amp;nbsp;do c&amp;ocirc; đồng s&amp;aacute;ng t&amp;aacute;c c&amp;ugrave;ng nh&amp;agrave; sản xuất Dan Nigro, lấy cảm hứng từ mối quan hệ giữa c&amp;ocirc; với Joshua Bassett. Giọng h&amp;aacute;t nữ t&amp;iacute;nh, chứa đựng cảm x&amp;uacute;c vụn vỡ của Olivia Rodrigo l&amp;agrave; một trong những yếu tố gi&amp;uacute;p b&amp;agrave;i h&amp;aacute;t th&amp;agrave;nh c&amp;ocirc;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Up - Cardi B&lt;/strong&gt;&lt;br /&gt;\r\nNg&amp;agrave;y ph&amp;aacute;t h&amp;agrave;nh: 5/2&lt;br /&gt;\r\nThể loại: Hip hop&lt;/p&gt;\r\n\r\n&lt;p&gt;Ca kh&amp;uacute;c l&amp;ocirc;i cuốn bởi nhịp điệu nhanh, gọn v&amp;agrave; lối đọc rap li&amp;ecirc;n tục, kh&amp;ocirc;ng ngừng nghỉ của Cardi B. Phần &amp;acirc;m nhạc mang nhiều &amp;acirc;m hưởng của drill, một nh&amp;aacute;nh nhỏ của nhạc trap bắt nguồn từ Chicago năm 2012. B&amp;agrave;i h&amp;aacute;t nhanh ch&amp;oacute;ng được ch&amp;uacute; &amp;yacute; kể từ khi Cardi B tr&amp;igrave;nh diễn lần đầu tại s&amp;acirc;n khấu Grammy 2021 hồi th&amp;aacute;ng 3. Sau đ&amp;oacute;,&amp;nbsp;&lt;em&gt;Up&lt;/em&gt;&amp;nbsp;trở th&amp;agrave;nh đĩa đơn thứ năm của Cardi B đứng đầu Billboard Hot 100 khi ra mắt, gi&amp;uacute;p c&amp;ocirc; trở th&amp;agrave;nh nữ rapper c&amp;oacute; nhiều ca kh&amp;uacute;c đạt th&amp;agrave;nh t&amp;iacute;ch đầu bảng nhất.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Leave the Door Open - Silk Sonic&lt;/strong&gt;&lt;br /&gt;\r\nNg&amp;agrave;y ph&amp;aacute;t h&amp;agrave;nh: 5/3&lt;br /&gt;\r\nThể loại: R&amp;amp;B, Soul&lt;/p&gt;\r\n\r\n&lt;p&gt;B&amp;agrave;i h&amp;aacute;t đ&amp;aacute;nh dấu sự trở lại của Bruno Mars trong vai tr&amp;ograve; th&amp;agrave;nh vi&amp;ecirc;n của nh&amp;oacute;m nhạc Silk Sonic, hợp t&amp;aacute;c c&amp;ugrave;ng Anderson .Paak. Ngay khi ra mắt, b&amp;agrave;i h&amp;aacute;t đứng đầu nhiều bảng xếp hạng tr&amp;ecirc;n thế giới, trong đ&amp;oacute; c&amp;oacute; Billboard Hot 100. Chất nhạc được đ&amp;aacute;nh gi&amp;aacute; cao nhờ kết hợp h&amp;agrave;i h&amp;ograve;a giữa nhạc soul thập ni&amp;ecirc;n 1970 v&amp;agrave; R&amp;amp;B những năm 2000. Giọng h&amp;aacute;t ngọt ng&amp;agrave;o của Bruno Mars h&amp;ograve;a quyện c&amp;ugrave;ng Anderson .Paak, tạo n&amp;ecirc;n cảm gi&amp;aacute;c thư gi&amp;atilde;n, dễ chịu. Đĩa đơn mở m&amp;agrave;n cho album đầu tay&amp;nbsp;&lt;em&gt;An Evening with Silk Sonic&lt;/em&gt;&amp;nbsp;của nh&amp;oacute;m dự kiến ph&amp;aacute;t h&amp;agrave;nh trong năm nay.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Peaches - Justin Bieber (feat. Daniel Caesar &amp;amp; Giveon)&lt;/strong&gt;&lt;br /&gt;\r\nNg&amp;agrave;y ph&amp;aacute;t h&amp;agrave;nh: 19/3&lt;br /&gt;\r\nThể loại: R&amp;amp;B&lt;/p&gt;\r\n\r\n&lt;p&gt;B&amp;agrave;i h&amp;aacute;t đứng đầu bảng Billboard Hot 100 ngay tuần đầu ph&amp;aacute;t h&amp;agrave;nh. Kh&amp;ocirc;ng chỉ th&amp;agrave;nh c&amp;ocirc;ng ở qu&amp;ecirc; nh&amp;agrave;,&amp;nbsp;&lt;em&gt;Peaches&lt;/em&gt;&amp;nbsp;c&amp;ograve;n lọt v&amp;agrave;o danh s&amp;aacute;ch y&amp;ecirc;u th&amp;iacute;ch ở nhiều quốc gia kh&amp;aacute;c như Anh, Australia, Canada, Đan Mạch... Ca kh&amp;uacute;c c&amp;oacute; giai điệu gần gũi v&amp;agrave; phần điệp kh&amp;uacute;c ngắn gọn, dễ nhớ. Lời b&amp;agrave;i h&amp;aacute;t do&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/chu-de/justin-bieber-2974&quot; rel=&quot;dofollow&quot;&gt;Justin Bieber&lt;/a&gt;&amp;nbsp;đồng s&amp;aacute;ng t&amp;aacute;c, ngợi ca t&amp;igrave;nh y&amp;ecirc;u l&amp;atilde;ng mạn, bền chặt. &amp;Acirc;m nhạc pha trộn R&amp;amp;B với c&amp;aacute;c nhạc cụ quen thuộc của jazz, soul.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;MONTERO (Call Me By Your Name) - Lil Nas X&lt;/strong&gt;&lt;br /&gt;\r\nNg&amp;agrave;y ph&amp;aacute;t h&amp;agrave;nh: 26/3&lt;br /&gt;\r\nThể loại: Hip hop, electropop&lt;/p&gt;\r\n\r\n&lt;p&gt;Ca kh&amp;uacute;c do rapper sinh năm 1999 Lil Nas X đồng s&amp;aacute;ng t&amp;aacute;c, nội dung k&amp;ecirc;u gọi mọi người dũng cảm sống thật với giới t&amp;iacute;nh của m&amp;igrave;nh. T&amp;ecirc;n b&amp;agrave;i được kết hợp từ t&amp;ecirc;n thật của nam ca sĩ (Montero Lamar Hill) v&amp;agrave; t&amp;ecirc;n bộ phim nổi tiếng&amp;nbsp;&lt;em&gt;Call Me By Your Name.&lt;/em&gt;&amp;nbsp;C&amp;aacute;c nh&amp;agrave; sản xuất đ&amp;atilde; kh&amp;eacute;o l&amp;eacute;o kết hợp nhạc electro-pop v&amp;agrave; giai điệu flamenco trong một ca kh&amp;uacute;c hip hop. B&amp;agrave;i h&amp;aacute;t kh&amp;ocirc;ng chỉ leo l&amp;ecirc;n vị tr&amp;iacute; số một Billboard Hot 100 m&amp;agrave; c&amp;ograve;n đứng đầu nhiều bảng xếp hạng &amp;acirc;m nhạc ở c&amp;aacute;c nước như Anh, Canada, Australia, Ph&amp;aacute;p...&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Kiss Me More - Doja Ca (feat. SZA)&lt;/strong&gt;&lt;br /&gt;\r\nNg&amp;agrave;y ph&amp;aacute;t h&amp;agrave;nh: 9/4&lt;br /&gt;\r\nThể loại: Hip hop, R&amp;amp;B&lt;/p&gt;\r\n\r\n&lt;p&gt;Đĩa đơn mở m&amp;agrave;n cho album thứ ba của Doja Cat mang t&amp;ecirc;n&amp;nbsp;&lt;em&gt;Planet Her&lt;/em&gt;&amp;nbsp;sẽ ph&amp;aacute;t h&amp;agrave;nh cuối th&amp;aacute;ng 6. B&amp;agrave;i h&amp;aacute;t đứng vị tr&amp;iacute; số một tại Malaysia, New Zealand v&amp;agrave; Singapore, đồng thời c&amp;oacute; mặt trong danh s&amp;aacute;ch được y&amp;ecirc;u th&amp;iacute;ch của nhiều quốc gia kh&amp;aacute;c. Tương tự bản hit&amp;nbsp;&lt;em&gt;Say So, Kiss Me More&lt;/em&gt;&amp;nbsp;c&amp;oacute; giai điệu nhẹ nh&amp;agrave;ng, dễ nhớ. Giọng h&amp;aacute;t của Doja Cat ngọt ng&amp;agrave;o, uyển chuyển. C&amp;ocirc; ho&amp;agrave;n th&amp;agrave;nh tốt cả hai vai tr&amp;ograve; h&amp;aacute;t lẫn đọc rap, kh&amp;ocirc;ng l&amp;eacute;p vế trước kh&amp;aacute;ch mời l&amp;agrave; SZA.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Save Your Tears (remix) - The Weeknd (feat. Ariana Grande)&lt;/strong&gt;&lt;br /&gt;\r\nNg&amp;agrave;y ph&amp;aacute;t h&amp;agrave;nh: 22/4&lt;br /&gt;\r\nThể loại: Synth-pop, synthwave&lt;/p&gt;\r\n\r\n&lt;p&gt;Tr&amp;iacute;ch từ album&amp;nbsp;&lt;em&gt;After Hours&lt;/em&gt;&amp;nbsp;(2020) của The Weeknd, ca kh&amp;uacute;c từng v&amp;agrave;o top 5 ở 30 quốc gia. Bản remix tiếp tục đưa ca kh&amp;uacute;c l&amp;ecirc;n vị tr&amp;iacute; đầu bảng Billboard, được l&amp;agrave;m mới với giai điệu nhẹ nh&amp;agrave;ng, thư gi&amp;atilde;n hơn.&amp;nbsp;&lt;em&gt;Save Your Tears&lt;/em&gt;&amp;nbsp;remix đ&amp;aacute;nh dấu lần cộng t&amp;aacute;c thứ ba giữa The Weeknd v&amp;agrave; Ariana Grande, sau&amp;nbsp;&lt;em&gt;Love Me Harder&lt;/em&gt;&amp;nbsp;(2014) v&amp;agrave;&amp;nbsp;&lt;em&gt;Off the Table&lt;/em&gt;&amp;nbsp;(2020). Trong một cuộc b&amp;igrave;nh chọn do Billboard tổ chức tr&amp;ecirc;n mạng v&amp;agrave;o th&amp;aacute;ng 4, bản remix đứng đầu danh s&amp;aacute;ch ca kh&amp;uacute;c được kh&amp;aacute;n giả y&amp;ecirc;u th&amp;iacute;ch nhất khi đ&amp;oacute;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Your Power - Billie Eilish&lt;/strong&gt;&lt;br /&gt;\r\nNg&amp;agrave;y ph&amp;aacute;t h&amp;agrave;nh: 29/4&lt;br /&gt;\r\nThể loại: Folk&lt;/p&gt;\r\n\r\n&lt;p&gt;Ca kh&amp;uacute;c do Billie Eilish s&amp;aacute;ng t&amp;aacute;c c&amp;ugrave;ng anh trai Finneas O&amp;#39;Connell - người thực hiện phần sản xuất. Lời h&amp;aacute;t m&amp;ocirc; tả những mối quan hệ kh&amp;ocirc;ng l&amp;agrave;nh mạnh, khi đ&amp;agrave;n &amp;ocirc;ng lạm dụng quyền lực với phụ nữ. &amp;Acirc;m nhạc nhẹ nh&amp;agrave;ng, chậm r&amp;atilde;i, được phối đơn giản bằng c&amp;aacute;c nhạc cụ acoustic như guitar, trống. Phong c&amp;aacute;ch n&amp;agrave;y ho&amp;agrave;n to&amp;agrave;n kh&amp;aacute;c biệt album đầu tay thi&amp;ecirc;n về nhạc điện tử của&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/chu-de/billie-eilish-2985&quot; rel=&quot;dofollow&quot;&gt;Billie Eilish&lt;/a&gt;.&amp;nbsp;&lt;em&gt;Your Power&lt;/em&gt;&amp;nbsp;l&amp;agrave; b&amp;agrave;i h&amp;aacute;t thứ ba của c&amp;ocirc; v&amp;agrave;o top 10 Billboard, sẽ c&amp;oacute; mặt trong album ph&amp;aacute;t h&amp;agrave;nh v&amp;agrave;o th&amp;aacute;ng 7 mang t&amp;ecirc;n&amp;nbsp;&lt;em&gt;Happier Than Ever.&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Butter - BTS&lt;/strong&gt;&lt;br /&gt;\r\nNg&amp;agrave;y ph&amp;aacute;t h&amp;agrave;nh: 21/5&lt;br /&gt;\r\nThể loại: Dance-pop, EDM&lt;/p&gt;\r\n\r\n&lt;p&gt;Ca kh&amp;uacute;c tiếng Anh thứ hai của&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/chu-de/bts-2600&quot; rel=&quot;dofollow&quot;&gt;BTS&lt;/a&gt;&amp;nbsp;sau th&amp;agrave;nh c&amp;ocirc;ng của&amp;nbsp;&lt;em&gt;&lt;a href=&quot;https://vnexpress.net/giai-tri/dynamite-cua-bts-dung-dau-billboard-4155211.html&quot; rel=&quot;dofollow&quot;&gt;Dynamite&lt;/a&gt;&lt;/em&gt;&amp;nbsp;năm ngo&amp;aacute;i. Ngay khi ra mắt,&amp;nbsp;&lt;em&gt;Butter&lt;/em&gt;&amp;nbsp;đ&amp;atilde; ph&amp;aacute; kỷ lục của Spotify, trở th&amp;agrave;nh ca kh&amp;uacute;c c&amp;oacute; lượt nghe nhiều nhất trong ng&amp;agrave;y đầu ti&amp;ecirc;n với hơn 11 triệu lượt. B&amp;agrave;i h&amp;aacute;t tiếp tục khai th&amp;aacute;c giai điệu rộn r&amp;agrave;ng, trẻ trung vốn l&amp;agrave; thế mạnh của nh&amp;oacute;m. Trả lời phỏng vấn tạp ch&amp;iacute;&amp;nbsp;&lt;em&gt;Rolling Stone&lt;/em&gt;, trưởng nh&amp;oacute;m RM n&amp;oacute;i đ&amp;acirc;y l&amp;agrave; một b&amp;agrave;i h&amp;aacute;t &amp;quot;gi&amp;agrave;u năng lượng&amp;quot; v&amp;agrave; &amp;quot;th&amp;iacute;ch hợp cho m&amp;ugrave;a h&amp;egrave;&amp;quot;.&amp;nbsp;&lt;em&gt;Butter&lt;/em&gt;&amp;nbsp;cũng nhanh ch&amp;oacute;ng vươn l&amp;ecirc;n vị tr&amp;iacute; đầu bảng Billboard, trở th&amp;agrave;nh đĩa đơn thứ tư của BTS đạt th&amp;agrave;nh t&amp;iacute;ch n&amp;agrave;y.&lt;/p&gt;\r\n', 'nhung-ca-khuc-u-my-noi-bat-nua-dau-nam', './uploads/images/1624904568_doja-cat-sza-1624848792-2280-1624849974.jpg', 1, 0, '2021-06-29 01:22:48', '2021-06-28 18:22:48'),
(15, 1, 22, 'Jennifer Lopez hạnh phúc bên Ben Affleck', '&lt;p&gt;Ca sĩ Jennifer Lopez rạng rỡ b&amp;ecirc;n Ben Affleck trong buổi hẹn h&amp;ograve; ở Los Angeles.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ng&amp;agrave;y 27/6,&amp;nbsp;&lt;em&gt;People&amp;nbsp;&lt;/em&gt;đăng h&amp;igrave;nh ảnh cặp sao ở ghế sau chiếc Rolls-Royce, sau khi d&amp;ugrave;ng bữa tại nh&amp;agrave; h&amp;agrave;ng Avra ở Beverly Hills, California. Trang n&amp;agrave;y cho hay họ thưởng thức đồ ăn Địa Trung Hải trong ph&amp;ograve;ng ri&amp;ecirc;ng khoảng hai giờ đồng hồ, rồi l&amp;aacute;i xe đi dạo. Nguồn tin cho biết: &amp;quot;Cả hai c&amp;oacute; t&amp;acirc;m trạng tuyệt vời. Ben hạnh ph&amp;uacute;c khi ở b&amp;ecirc;n Jen. C&amp;ocirc; ấy d&amp;agrave;nh nhiều thời gian cho Ben nhất c&amp;oacute; thể&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;Jennifer Lopez rạng rỡ b&ecirc;n Ben Affleck sau khi rời khỏi nh&agrave; h&agrave;ng. Ảnh: Backgrid.&quot; src=&quot;https://i1-giaitri.vnecdn.net/2021/06/27/jen-ben-3-2495-1624757863.jpg?w=680&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=SfJIPwckDk_aOsbTvd_kMQ&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-size:10px&quot;&gt;Jennifer Lopez rạng rỡ b&amp;ecirc;n Ben Affleck sau khi rời khỏi nh&amp;agrave; h&amp;agrave;ng. Ảnh:&amp;nbsp;&lt;em&gt;Backgrid.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Từ khi t&amp;aacute;i hợp hồi cuối th&amp;aacute;ng 4, cặp t&amp;igrave;nh nh&amp;acirc;n tận hưởng khoảng thời gian b&amp;ecirc;n nhau ở Los Angeles - nơi Affleck sống - v&amp;agrave; Miami - nh&amp;agrave; của Jennifer. Đầu th&amp;aacute;ng 6, cả hai bị bắt gặp&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/jennifer-lopez-hon-ben-affleck-4294246.html&quot; rel=&quot;dofollow&quot;&gt;h&amp;ocirc;n nhau&lt;/a&gt;&amp;nbsp;khi dự tiệc sinh nhật của Linda Lopez - chị g&amp;aacute;i Jennifer - tại nh&amp;agrave; h&amp;agrave;ng Nobu ở Malibu, California. Tại đ&amp;acirc;y, ca sĩ giới thiệu hai con với Ben Affleck. &amp;quot;C&amp;aacute;c con của c&amp;ocirc; ấy thực sự th&amp;iacute;ch Ben. Sự h&amp;agrave;i hước của anh ấy khiến ch&amp;uacute;ng thấy vui khi ở b&amp;ecirc;n cạnh. Ben h&amp;ograve;a to&amp;agrave;n thu phục được gia đ&amp;igrave;nh bạn g&amp;aacute;i nhờ sự quyến rũ của m&amp;igrave;nh&amp;quot;, một nguồn tin n&amp;oacute;i tr&amp;ecirc;n&amp;nbsp;&lt;em&gt;Eonline&lt;/em&gt;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Jennifer Lopez thường xuy&amp;ecirc;n gặp gỡ t&amp;igrave;nh cũ Ben Affleck sau khi&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/giai-tri/jennifer-lopez-huy-hon-4247855.html&quot; rel=&quot;dofollow&quot;&gt;hủy h&amp;ocirc;n&lt;/a&gt;&amp;nbsp;Alex Rodriguez. Cặp sao kh&amp;ocirc;ng x&amp;aacute;c nhận mối quan hệ nhưng nguồn tin th&amp;acirc;n cận cho biết họ nghi&amp;ecirc;m t&amp;uacute;c v&amp;agrave; t&amp;iacute;nh chuyện gắn b&amp;oacute; l&amp;acirc;u d&amp;agrave;i. Lopez cũng quyết định c&amp;ugrave;ng hai con chuyển nh&amp;agrave; từ Miami đến Los Angeles để ở gần Affleck. T&amp;agrave;i tử Batman hiện ở bang California để nu&amp;ocirc;i con chung với vợ cũ&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/giai-tri/jennifer-garner-han-gan-tinh-cu-4273039.html&quot; rel=&quot;dofollow&quot;&gt;Jennifer Garner.&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;a href=&quot;https://vnexpress.net/chu-de/jennifer-lopez-2971&quot; rel=&quot;dofollow&quot;&gt;Jennifer Lopez&lt;/a&gt;&amp;nbsp;v&amp;agrave;&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/chu-de/ben-affleck-3519&quot; rel=&quot;dofollow&quot;&gt;Ben Affleck&lt;/a&gt;&amp;nbsp;từng đ&amp;iacute;nh h&amp;ocirc;n năm 2002, được nhiều người đặt biệt danh &amp;quot;Bennifer&amp;quot;. Mối t&amp;igrave;nh từng được ch&amp;uacute; &amp;yacute; bậc nhất Hollywood trước khi họ chia tay th&amp;aacute;ng 1/2004, v&amp;agrave;i ng&amp;agrave;y trước lễ cưới. Năm 2017, Jennifer Lopez thổ lộ mất hai năm để qu&amp;ecirc;n đi nỗi đau tan vỡ sau khi hủy h&amp;ocirc;n Affleck.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;&lt;strong&gt;Hiểu Nh&amp;acirc;n&lt;/strong&gt;&lt;/p&gt;\r\n', 'jennifer-lopez-hanh-phuc-ben-ben-affleck', './uploads/images/1624904829_jen-ben-3-2495-1624757863.jpg', 1, 0, '2021-06-29 01:27:09', '2021-06-28 18:27:09'),
(16, 1, 17, 'Hà Nội công bố điểm thi vào lớp 10', '&lt;p&gt;Chiều 26/6, Sở Gi&amp;aacute;o dục v&amp;agrave; Đ&amp;agrave;o tạo H&amp;agrave; Nội c&amp;ocirc;ng bố điểm thi v&amp;agrave;o lớp 10 THPT c&amp;ocirc;ng lập của hơn 93.000 th&amp;iacute; sinh, sớm hơn 4 ng&amp;agrave;y so với kế hoạch.&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&amp;iacute; sinh dự thi v&amp;agrave;o lớp 10 năm nay c&amp;oacute; thể tra cứu điểm thi tr&amp;ecirc;n Cổng th&amp;ocirc;ng tin điện tử của Sở Gi&amp;aacute;o dục v&amp;agrave; Đ&amp;agrave;o tạo (http://hanoi.edu.vn), tra qua sổ li&amp;ecirc;n lạc điện tử hoặc qua hệ thống hỗ trợ 1080.&lt;/p&gt;\r\n\r\n&lt;p&gt;Căn cứ v&amp;agrave;o điểm b&amp;agrave;i thi c&amp;aacute;c m&amp;ocirc;n, học sinh c&amp;oacute; nguyện vọng ph&amp;uacute;c khảo b&amp;agrave;i thi, l&amp;agrave;m đơn gửi tới trường THCS, nơi học lớp 9. Thời gian nộp đơn ph&amp;uacute;c khảo từ ng&amp;agrave;y 30/6 đến 6/7.&lt;/p&gt;\r\n\r\n&lt;p&gt;Đại diện Sở Gi&amp;aacute;o dục v&amp;agrave; Đ&amp;agrave;o tạo H&amp;agrave; Nội cho biết, do c&amp;ocirc;ng bố điểm thi sớm hơn dự kiến, Sở đẩy việc c&amp;ocirc;ng bố điểm chuẩn l&amp;ecirc;n sớm. Dự kiến chiều tối 28/6, Sở sẽ c&amp;ocirc;ng bố điểm chuẩn v&amp;agrave;o lớp 10 THPT c&amp;ocirc;ng lập cả chuy&amp;ecirc;n v&amp;agrave; kh&amp;ocirc;ng chuy&amp;ecirc;n.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;Th&iacute; sinh thi v&agrave;o lớp 10 tại điểm thi trường THPT chuy&ecirc;n H&agrave; Nội - Amsterdam h&ocirc;m 12/6. Ảnh: Dương T&acirc;m.&quot; src=&quot;https://i1-vnexpress.vnecdn.net/2021/06/26/thi-sinh-thi-vao-10-8365-1624701841.jpg?w=680&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=TNv85ecZ4_FYNECNurnZIw&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-size:12px&quot;&gt;Th&amp;iacute; sinh thi v&amp;agrave;o lớp 10 tại điểm thi trường THPT chuy&amp;ecirc;n H&amp;agrave; Nội - Amsterdam h&amp;ocirc;m 12/6. Ảnh:&amp;nbsp;&lt;em&gt;Dương T&amp;acirc;m.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Sau hai lần l&amp;ugrave;i, ng&amp;agrave;y 12-13/6, hơn 93.000 th&amp;iacute; sinh H&amp;agrave; Nội thi 4 m&amp;ocirc;n Ngữ văn, Ngoại ngữ, To&amp;aacute;n, Lịch sử để gi&amp;agrave;nh 67.000 suất v&amp;agrave;o lớp 10 c&amp;ocirc;ng lập. 26.000 học sinh c&amp;ograve;n lại sẽ học trường ngo&amp;agrave;i c&amp;ocirc;ng lập, trường THPT c&amp;ocirc;ng lập tự chủ t&amp;agrave;i ch&amp;iacute;nh, trung t&amp;acirc;m Gi&amp;aacute;o dục nghề nghiệp - Gi&amp;aacute;o dục thường xuy&amp;ecirc;n v&amp;agrave; học nghề.&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&amp;iacute; sinh l&amp;agrave;m bốn b&amp;agrave;i thi gồm To&amp;aacute;n, Ngữ văn, Ngoại ngữ v&amp;agrave; Lịch sử. Điểm x&amp;eacute;t tuyển sẽ được t&amp;iacute;nh bằng tổng điểm To&amp;aacute;n v&amp;agrave; Ngữ văn nh&amp;acirc;n hệ số 2, cộng với điểm Ngoại ngữ, Lịch sử v&amp;agrave; điểm ưu ti&amp;ecirc;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo quy định, th&amp;iacute; sinh được đăng k&amp;yacute; tối đa 3 nguyện vọng, nhiều hơn năm ngo&amp;aacute;i 1. Nếu trượt nguyện vọng 1, th&amp;iacute; sinh sẽ được x&amp;eacute;t tuyển nguyện vọng 2 nhưng phải c&amp;oacute; điểm cao hơn điểm chuẩn của trường &amp;iacute;t nhất 1 điểm. Nếu kh&amp;ocirc;ng tr&amp;uacute;ng tuyển nguyện vọng 2, th&amp;iacute; sinh được x&amp;eacute;t tuyển nguyện vọng 3, nhưng phải c&amp;oacute; điểm cao hơn điểm chuẩn nguyện vọng 1 của trường &amp;iacute;t nhất 2 điểm.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nếu như những năm trước, khi hạ điểm chuẩn, nh&amp;agrave; trường chỉ nhận học sinh đăng k&amp;yacute; nguyện vọng 1 th&amp;igrave; năm nay sẽ nhận cả học sinh c&amp;oacute; nguyện vọng 2, 3 v&amp;agrave; đủ điều kiện tr&amp;uacute;ng tuyển.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:right&quot;&gt;&lt;strong&gt;Dương T&amp;acirc;m&lt;/strong&gt;&lt;/p&gt;\r\n', 'ha-noi-cong-bo-diem-thi-vao-lop-10', './uploads/images/1624904976_thi-sinh-thi-vao-10-1624700609-8083-1624701842.jpg', 1, 0, '2021-06-29 01:29:36', '2021-06-28 18:29:36'),
(17, 1, 6, 'TP HCM lấy ý kiến phụ huynh về phương án thi tốt nghiệp', '&lt;p&gt;Đến 19h ng&amp;agrave;y 28/6, hơn 89.200 phụ huynh ở TP HCM sẽ ho&amp;agrave;n tất việc đưa ra &amp;yacute; kiến c&amp;oacute; hay kh&amp;ocirc;ng đồng &amp;yacute; cho con thi tốt nghiệp THPT đợt 1 ng&amp;agrave;y 7-8/7.&lt;/p&gt;\r\n\r\n&lt;p&gt;Chiều 28/6, phiếu khảo s&amp;aacute;t lấy &amp;yacute; kiến phụ huynh được Sở Gi&amp;aacute;o dục v&amp;agrave; Đ&amp;agrave;o tạo ph&amp;aacute;t đi với 3 lựa chọn:&amp;nbsp;&lt;em&gt;y&amp;ecirc;n t&amp;acirc;m v&amp;agrave; đồng &amp;yacute;&lt;/em&gt;&amp;nbsp;cho con&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/chu-de/thi-tot-nghiep-thpt-2021-3336&quot; rel=&quot;dofollow&quot;&gt;thi tốt nghiệp THPT năm 2021&lt;/a&gt;&amp;nbsp;đợt 1;&amp;nbsp;&lt;em&gt;kh&amp;ocirc;ng y&amp;ecirc;n t&amp;acirc;m nhưng vẫn đồng &amp;yacute;;&amp;nbsp;&lt;/em&gt;&lt;em&gt;kh&amp;ocirc;ng y&amp;ecirc;n t&amp;acirc;m v&amp;agrave; kh&amp;ocirc;ng đồng &amp;yacute;&lt;/em&gt;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Động th&amp;aacute;i n&amp;agrave;y được đưa ra để Sở c&amp;oacute; cơ sở xem x&amp;eacute;t to&amp;agrave;n diện khi quyết định tổ chức thi tốt nghiệp THPT, tr&amp;aacute;nh g&amp;acirc;y t&amp;acirc;m l&amp;yacute; hoang mang, lo lắng, ảnh hưởng đến kết quả thi của th&amp;iacute; sinh.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c trường c&amp;oacute; thể lấy &amp;yacute; kiến phụ huynh hoặc người gi&amp;aacute;m hộ học sinh qua c&amp;aacute;c nh&amp;oacute;m của lớp học, Google Form rồi tổng hợp, b&amp;aacute;o c&amp;aacute;o Sở.&lt;/p&gt;\r\n\r\n&lt;p&gt;S&amp;aacute;ng c&amp;ugrave;ng ng&amp;agrave;y, UBND TP HCM họp Ban chỉ đạo ph&amp;ograve;ng chống Covid-19, thống nhất sẽ quyết định phương &amp;aacute;n thi tốt nghiệp THPT v&amp;agrave;o ng&amp;agrave;y 30/6 - ng&amp;agrave;y cuối thực hiện gi&amp;atilde;n c&amp;aacute;ch x&amp;atilde; hội to&amp;agrave;n th&amp;agrave;nh phố.&lt;/p&gt;\r\n\r\n&lt;p&gt;L&amp;atilde;nh đạo Sở Gi&amp;aacute;o dục v&amp;agrave; Đ&amp;agrave;o tạo cho biết, th&amp;agrave;nh phố y&amp;ecirc;u cầu c&amp;aacute;c ng&amp;agrave;nh li&amp;ecirc;n quan x&amp;acirc;y dựng cụ thể phương &amp;aacute;n tổ chức x&amp;eacute;t nghiệm, s&amp;agrave;ng lọc cho tất cả th&amp;iacute; sinh, c&amp;aacute;n bộ, gi&amp;aacute;o vi&amp;ecirc;n v&amp;agrave; nh&amp;acirc;n vi&amp;ecirc;n coi thi tại 155 điểm thi (hơn 104.000 người) nếu thi đợt 1 như đơn vị đ&amp;atilde;&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/so-giao-duc-va-dao-tao-tp-hcm-de-xuat-thi-tot-nghiep-dot-1-4300357.html&quot; rel=&quot;dofollow&quot;&gt;đề xuất.&lt;/a&gt;&amp;nbsp;Trong tuần n&amp;agrave;y, Sở tập huấn tổ chức thi cho c&amp;aacute;c trưởng điểm thi v&amp;agrave; nghiệp vụ thanh tra.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;Gi&aacute;m thị tại điểm thi THPT Marie Curie phổ biến quy chế thi tốt nghiệp THPT năm 2020. Ảnh: Hữu Khoa.&quot; src=&quot;https://i1-vnexpress.vnecdn.net/2021/06/28/thi-tot-nghiep-5410-1624860102.jpg?w=680&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=mNxZDpNztb7pV_7aJRdCHA&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-size:12px&quot;&gt;Gi&amp;aacute;m thị tại điểm thi THPT Marie Curie phổ biến quy chế thi tốt nghiệp THPT năm 2020. Ảnh:&amp;nbsp;&lt;em&gt;Hữu Khoa.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo hiệu trưởng c&amp;aacute;c trường THPT, c&amp;ocirc;ng t&amp;aacute;c chuẩn bị thi đang ho&amp;agrave;n tất theo kế hoạch thi đợt 1 (ng&amp;agrave;y 7 v&amp;agrave; 8/7). Những th&amp;iacute; sinh ở nơi bị phong toả hoặc thực hiện c&amp;aacute;ch ly x&amp;atilde; hội, nh&amp;oacute;m F0-F2 sẽ dự thi tốt nghiệp đợt 2 (lịch được th&amp;ocirc;ng b&amp;aacute;o sau).&lt;/p&gt;\r\n\r\n&lt;p&gt;Để gi&amp;uacute;p ng&amp;agrave;nh gi&amp;aacute;o dục r&amp;agrave; so&amp;aacute;t th&amp;iacute; sinh ở nơi bị phong toả, thuộc nh&amp;oacute;m F0-F1, gi&amp;aacute;o vi&amp;ecirc;n chủ nhiệm sẽ li&amp;ecirc;n lạc với học sinh hằng ng&amp;agrave;y để cập nhật th&amp;ocirc;ng tin. Khi c&amp;oacute; kế hoạch x&amp;eacute;t nghiệm cho th&amp;iacute; sinh, c&amp;aacute;c trường sẽ triển khai th&amp;ocirc;ng tin đến c&amp;aacute;c em, hỗ trợ nh&amp;acirc;n sự điều phối, gi&amp;uacute;p ng&amp;agrave;nh y tế lấy mẫu.&lt;/p&gt;\r\n\r\n&lt;p&gt;TP HCM đ&amp;atilde; huy động hơn 15.800 người để phục vụ kỳ thi; trong đ&amp;oacute; 400 nguời l&amp;agrave; thanh tra thi; c&amp;aacute;n bộ chấm thi l&amp;agrave; hơn 1.700; c&amp;aacute;n bộ chấm ph&amp;uacute;c khảo 1.000 người.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nếu UBND TP HCM chốt thi đợt 1, ng&amp;agrave;y 2/7 (tức 5 ng&amp;agrave;y trước kỳ thi) sẽ diễn ra buổi tổng dợt phương &amp;aacute;n thi an to&amp;agrave;n. Sở Y tế hỗ trợ nh&amp;acirc;n sự để lấy x&amp;eacute;t nghiệm to&amp;agrave;n bộ c&amp;aacute;n bộ, gi&amp;aacute;o vi&amp;ecirc;n, nh&amp;acirc;n vi&amp;ecirc;n v&amp;agrave; th&amp;iacute; sinh; trưởng điểm thi triển khai phương &amp;aacute;n thi an to&amp;agrave;n gồm: ph&amp;acirc;n luồng, đo th&amp;acirc;n nhiệt, rửa tay s&amp;aacute;t khuẩn, hướng dẫn học sinh gi&amp;atilde;n c&amp;aacute;ch ra v&amp;agrave;o điểm thi...&lt;/p&gt;\r\n\r\n&lt;p&gt;Mỗi điểm thi c&amp;oacute; từ 20 đến 35 ph&amp;ograve;ng với 380-800 th&amp;iacute; sinh; kh&amp;ocirc;ng qu&amp;aacute; 130 c&amp;aacute;n bộ, gi&amp;aacute;o vi&amp;ecirc;n v&amp;agrave; nh&amp;acirc;n vi&amp;ecirc;n coi thi. Mỗi điểm c&amp;oacute; &amp;iacute;t nhất 2 ph&amp;ograve;ng thi dự ph&amp;ograve;ng cho c&amp;aacute;c em c&amp;oacute; dấu hiệu bệnh, ho, sốt; mỗi quận huyện, TP Thủ Đức sẽ c&amp;oacute; 1-3 điểm thi dự ph&amp;ograve;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ph&amp;ograve;ng thi kh&amp;ocirc;ng qu&amp;aacute; 24 th&amp;iacute; sinh, kh&amp;ocirc;ng sử dụng m&amp;aacute;y lạnh. Mỗi điểm thi ph&amp;acirc;n &amp;iacute;t nhất 4 luồng để đo th&amp;acirc;n nhiệt th&amp;iacute; sinh theo ph&amp;ograve;ng thi; th&amp;iacute; sinh ra về ngay sau khi thi, kh&amp;ocirc;ng tụ tập.&lt;/p&gt;\r\n\r\n&lt;p&gt;Về c&amp;ocirc;ng t&amp;aacute;c an ninh, C&amp;ocirc;ng an TP HCM sẽ cử 2 c&amp;ocirc;ng an l&amp;agrave;m việc trong mỗi điểm thi, 2 c&amp;ocirc;ng an bảo vệ trật tự b&amp;ecirc;n ngo&amp;agrave;i. Ngo&amp;agrave;i ra, mỗi điểm thi sẽ c&amp;oacute; th&amp;ecirc;m &amp;iacute;t nhất 6 nh&amp;acirc;n sự l&amp;agrave; c&amp;ocirc;ng an phường, x&amp;atilde;, d&amp;acirc;n ph&amp;ograve;ng, bảo vệ khu phố hướng dẫn ph&amp;acirc;n luồng, đảm bảo gi&amp;atilde;n c&amp;aacute;ch, tr&amp;aacute;nh tụ tập đ&amp;ocirc;ng người.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hiện, TP HCM ghi nhận hơn 3.200 ca Covid-19, nhiều thứ hai cả nước, sau Bắc Giang. Một tuần trước, c&amp;aacute;c gi&amp;aacute;o vi&amp;ecirc;n, phụ huynh v&amp;agrave; th&amp;iacute; sinh b&amp;agrave;y tỏ sự lo ngại khi kỳ thi diễn ra trong bối cảnh dịch bệnh phức tạp, kh&amp;oacute; lường. Tuy nhi&amp;ecirc;n, l&amp;atilde;nh đạo TP HCM đặt quyết t&amp;acirc;m để kỳ thi &amp;quot;diễn ra an to&amp;agrave;n tuyệt đối&amp;quot;.&lt;/p&gt;\r\n', 'tp-hcm-lay-y-kien-phu-huynh-ve-phuong-an-thi-tot-nghiep', './uploads/images/1624905066_thi-tot-nghiep-5410-1624860102.jpg', 1, 1, '2021-06-29 01:31:06', '2021-06-28 18:48:15'),
(18, 4, 17, '10 trường có điểm chuẩn vào lớp 10 cao nhất Hà Nội', '&lt;p&gt;Top 10 gồm những c&amp;aacute;i t&amp;ecirc;n quen thuộc như THPT Chu Văn An, Kim Li&amp;ecirc;n, Y&amp;ecirc;n H&amp;ograve;a, trong đ&amp;oacute; điểm tr&amp;uacute;ng tuyển v&amp;agrave;o trường THPT Chu Văn An cao kỷ lục 53,3.&lt;/p&gt;\r\n\r\n&lt;p&gt;THPT Chu Văn An nhiều năm li&amp;ecirc;n tiếp dẫn đầu trong hơn 100 trường THPT c&amp;ocirc;ng lập kh&amp;ocirc;ng chuy&amp;ecirc;n ở H&amp;agrave; Nội về chất lượng học sinh đầu v&amp;agrave;o. T&amp;iacute;nh theo c&amp;ocirc;ng thức điểm To&amp;aacute;n v&amp;agrave; Ngữ văn nh&amp;acirc;n hệ số 2 cộng điểm Ngoại ngữ v&amp;agrave; Lịch sử, trung b&amp;igrave;nh mỗi m&amp;ocirc;n học sinh phải đạt tr&amp;ecirc;n 8,88 mới tr&amp;uacute;ng tuyển. So với mức trung b&amp;igrave;nh năm ngo&amp;aacute;i, mức n&amp;agrave;y cao hơn 0,23 v&amp;agrave; cao hơn năm kia 0,75 điểm.&lt;/p&gt;\r\n\r\n&lt;p&gt;Với bề d&amp;agrave;y th&amp;agrave;nh t&amp;iacute;ch, c&amp;oacute; lớp chuy&amp;ecirc;n với đội ngũ gi&amp;aacute;o vi&amp;ecirc;n giỏi, khu&amp;ocirc;n vi&amp;ecirc;n c&amp;ugrave;ng cơ sở vật chất tốt, trường THPT Chu Văn An được tuyển sinh to&amp;agrave;n th&amp;agrave;nh phố (kh&amp;ocirc;ng theo khu vực như trường kh&amp;aacute;c) n&amp;ecirc;n tỷ lệ chọi cũng cao nhất - 1/2,98.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tương tự năm ngo&amp;aacute;i, vị tr&amp;iacute; thứ hai thuộc về trường THPT Kim Li&amp;ecirc;n với điểm chuẩn 50,25, tức trung b&amp;igrave;nh điểm mỗi m&amp;ocirc;n cũng phải 8,375. Trường Y&amp;ecirc;n H&amp;ograve;a tiếp tục đứng thứ ba với 50 điểm. Bảy trường c&amp;ograve;n lại trong top 10 đều lấy điểm tr&amp;uacute;ng tuyển từ 48 trở l&amp;ecirc;n, trong đ&amp;oacute; trường THPT Nh&amp;acirc;n Ch&amp;iacute;nh thấp nhất.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;/thehours/uploads/images/10-trng-thpt-c-im-chun-c.png&quot; style=&quot;height:400px; width:600px&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;So với năm 2020, những c&amp;aacute;i t&amp;ecirc;n trong top 10 kh&amp;ocirc;ng thay đổi nhưng thứ tự c&amp;oacute; sự dịch chuyển. Chẳng hạn, năm ngo&amp;aacute;i trường Phan Đ&amp;igrave;nh Ph&amp;ugrave;ng c&amp;oacute; điểm tr&amp;uacute;ng tuyển cao thứ 3, chỉ sau Chu Văn An v&amp;agrave; Kim Li&amp;ecirc;n, nhưng năm nay đ&amp;atilde; tụt xuống thứ 5, đổi vị tr&amp;iacute; với trường Y&amp;ecirc;n H&amp;ograve;a. Trường THPT Thăng Long cũng tụt từ vị tr&amp;iacute; thứ 4 trong top 10 xuống thứ 8.&lt;/p&gt;\r\n\r\n&lt;p&gt;T&amp;iacute;nh theo khu vực tuyển sinh, khu vực 3 (gồm quận Đống Đa, Thanh Xu&amp;acirc;n, Cầu Giấy) c&amp;oacute; nhiều trường trong top 10 nhất, gồm THPT Kim Li&amp;ecirc;n, Y&amp;ecirc;n H&amp;ograve;a v&amp;agrave; Nh&amp;acirc;n Ch&amp;iacute;nh. Khu vực 2 (gồm quận Ho&amp;agrave;n Kiếm v&amp;agrave; Hai B&amp;agrave; Trưng) c&amp;oacute; hai trường l&amp;agrave; THPT Thăng Long v&amp;agrave; Việt Đức.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c trường c&amp;ograve;n lại l&amp;agrave; L&amp;ecirc; Qu&amp;yacute; Đ&amp;ocirc;n - H&amp;agrave; Đ&amp;ocirc;ng, Nguyễn Thị Minh Khai, Phan Đ&amp;igrave;nh Ph&amp;ugrave;ng lần lượt thuộc khu vực 10 (H&amp;agrave; Đ&amp;ocirc;ng, Chương Mỹ, Thanh Oai), 7 (Bắc Từ Li&amp;ecirc;m, Nam Từ Li&amp;ecirc;m, Ho&amp;agrave;i Đức, Đan Phượng) v&amp;agrave; 1 (Ba Đ&amp;igrave;nh, T&amp;acirc;y Hồ).&lt;/p&gt;\r\n\r\n&lt;p&gt;H&amp;agrave; Nội c&amp;oacute; số th&amp;iacute; sinh thi v&amp;agrave;o lớp 10 đ&amp;ocirc;ng nhất cả nước, với hơn 93.000. Sau hai lần l&amp;ugrave;i thi v&amp;agrave; điều chỉnh thời gian l&amp;agrave;m b&amp;agrave;i do Covid-19, th&amp;agrave;nh phố đ&amp;atilde; tổ chức th&amp;agrave;nh c&amp;ocirc;ng kỳ thi v&amp;agrave;o ng&amp;agrave;y 12-13/6. Th&amp;iacute; sinh l&amp;agrave;m 4 b&amp;agrave;i thi gồm Ngữ văn, Ngoại ngữ, To&amp;aacute;n, Lịch sử để gi&amp;agrave;nh 67.000 suất v&amp;agrave;o lớp 10 c&amp;ocirc;ng lập.&lt;/p&gt;\r\n\r\n&lt;p&gt;Điểm x&amp;eacute;t tuyển sẽ được t&amp;iacute;nh bằng tổng điểm To&amp;aacute;n v&amp;agrave; Ngữ văn nh&amp;acirc;n hệ số 2, cộng với điểm Ngoại ngữ, Lịch sử v&amp;agrave; điểm ưu ti&amp;ecirc;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo quy định, th&amp;iacute; sinh được đăng k&amp;yacute; tối đa 3 nguyện vọng, nhiều hơn năm ngo&amp;aacute;i 1. Nếu trượt nguyện vọng 1, th&amp;iacute; sinh sẽ được x&amp;eacute;t tuyển nguyện vọng 2 nhưng phải c&amp;oacute; điểm cao hơn điểm chuẩn của trường &amp;iacute;t nhất 1 điểm. Nếu kh&amp;ocirc;ng tr&amp;uacute;ng tuyển nguyện vọng 2, c&amp;aacute;c em được x&amp;eacute;t tuyển nguyện vọng 3, nhưng phải c&amp;oacute; điểm cao hơn điểm chuẩn nguyện vọng 1 của trường &amp;iacute;t nhất 2 điểm.&lt;/p&gt;\r\n', '10-truong-co-diem-chuan-vao-lop-10-cao-nhat-ha-noi', './uploads/images/1624905227_10-trng-thpt-c-im-chun-c.png', 1, 2, '2021-06-29 01:33:47', '2021-06-28 18:34:35'),
(19, 4, 17, '10 trường THPT ở Hà Nội lấy 3-4 điểm mỗi môn', '&lt;p&gt;Năm 2021, điểm chuẩn v&amp;agrave;o lớp 10 của hai trường THPT Minh Quang v&amp;agrave; Bất Bạt, huyện Ba V&amp;igrave;, l&amp;agrave; 18,05, tức xấp xỉ 3 điểm mỗi m&amp;ocirc;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tối 28/6, Sở Gi&amp;aacute;o dục v&amp;agrave; Đ&amp;agrave;o tạo H&amp;agrave; Nội c&amp;ocirc;ng bố&amp;nbsp;&lt;a href=&quot;https://vnexpress.net/ha-noi-cong-bo-diem-chuan-vao-lop-10-thpt-cong-lap-4300885.html&quot; rel=&quot;dofollow&quot;&gt;điểm chuẩn v&amp;agrave;o lớp 10&lt;/a&gt;&amp;nbsp;của 115 trường THPT c&amp;ocirc;ng lập kh&amp;ocirc;ng chuy&amp;ecirc;n. Điểm x&amp;eacute;t tuyển sẽ được t&amp;iacute;nh bằng tổng điểm To&amp;aacute;n v&amp;agrave; Ngữ văn nh&amp;acirc;n hệ số 2, cộng với điểm Ngoại ngữ, Lịch sử v&amp;agrave; điểm ưu ti&amp;ecirc;n. Nếu chỉ t&amp;iacute;nh điểm thi, th&amp;iacute; sinh c&amp;oacute; thể gi&amp;agrave;nh tối đa 60 điểm.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trường THPT Chu Văn An lấy điểm chuẩn cao nhất - 53,3, nghĩa l&amp;agrave; th&amp;iacute; sinh phải đạt 8,88 điểm mỗi m&amp;ocirc;n mới c&amp;oacute; thể tr&amp;uacute;ng tuyển. Ngo&amp;agrave;i ra, hai trường kh&amp;aacute;c cũng lấy điểm chuẩn từ 50 trở l&amp;ecirc;n l&amp;agrave; Y&amp;ecirc;n H&amp;ograve;a (50), Kim Li&amp;ecirc;n (50,25).&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong khi đ&amp;oacute;, 10 trường trong top thấp nhất H&amp;agrave; Nội lấy điểm chuẩn 18,05 - 24. Minh Quang v&amp;agrave; Bất Bạt đứng ch&amp;oacute;t, trung b&amp;igrave;nh chỉ 3,01 điểm mỗi m&amp;ocirc;n. Năm ngo&amp;aacute;i, hai trường n&amp;agrave;y cũng lấy điểm chuẩn bằng nhau - 13 (trung b&amp;igrave;nh 2,6 điểm một m&amp;ocirc;n), chỉ cao hơn THPT Đại Cường 0,5 điểm.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;/thehours/uploads/images/10-trng-thpt-ly-im-chun.png&quot; style=&quot;height:400px; width:600px&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Xếp tr&amp;ecirc;n Minh Quang v&amp;agrave; Bất Bạt l&amp;agrave; THPT Nguyễn Văn Trỗi v&amp;agrave; THPT Mỹ Đức C, c&amp;ugrave;ng lấy 20 điểm. Trừ trường Nguyễn Văn Trỗi tuyển sinh năm đầu ti&amp;ecirc;n, trường Mỹ Đức C giữ nguy&amp;ecirc;n mức điểm trung b&amp;igrave;nh 3,3 mỗi m&amp;ocirc;n của năm ngo&amp;aacute;i, d&amp;ugrave; hầu hết trường đều tăng điểm chuẩn.&lt;/p&gt;\r\n\r\n&lt;p&gt;Bắc Lương Sơn v&amp;agrave; Lưu Ho&amp;agrave;ng c&amp;ugrave;ng đứng thứ 5 trong top 10 trường c&amp;oacute; điểm chuẩn thấp nhất H&amp;agrave; Nội với 21. D&amp;ugrave; thấp, điểm chuẩn của trường Lưu Ho&amp;agrave;ng đ&amp;atilde; tăng từ 2,6 mỗi m&amp;ocirc;n của năm ngo&amp;aacute;i l&amp;ecirc;n 3,5 của năm nay. Trong khi đ&amp;oacute;, trường Bắc Lương Sơn lại giảm nhẹ, từ 3,6 xuống 3,5.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c trường THPT c&amp;ograve;n lại của top 10 thấp nhất gồm Đại Cường (22), Ứng H&amp;ograve;a B (22,5), Ba V&amp;igrave; v&amp;agrave; Hợp Thanh (c&amp;ugrave;ng 24). Năm ngo&amp;aacute;i, Đại Cường lấy điểm chuẩn thấp nhất H&amp;agrave; Nội, chỉ 2,5 mỗi m&amp;ocirc;n, thấp hơn năm nay 1,7 điểm. Ba trường c&amp;ograve;n lại gần như kh&amp;ocirc;ng thay đổi điểm chuẩn trung b&amp;igrave;nh m&amp;ocirc;n so với năm trước, dao động 3,8-4.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong 10 trường n&amp;agrave;y, khu vực 12 (gồm huyện Mỹ Đức, Ứng H&amp;ograve;a) c&amp;oacute; đến năm đại diện. Đ&amp;acirc;y cũng l&amp;agrave; khu vực thường xuy&amp;ecirc;n c&amp;oacute; trường lấy điểm thấp nhất H&amp;agrave; Nội. Khu vực 8 (Ph&amp;uacute;c Thọ, Sơn T&amp;acirc;y, Ba V&amp;igrave;) c&amp;oacute; ba trường, khu vực 9 (Thạch Thất, Quốc Oai) v&amp;agrave; 10 (H&amp;agrave; Đ&amp;ocirc;ng, Chương Mỹ, Thanh Oai) mỗi nơi c&amp;oacute; một trường. Những trường n&amp;agrave;y nằm ở c&amp;aacute;c khu vực ngoại th&amp;agrave;nh, c&amp;aacute;ch trung t&amp;acirc;m H&amp;agrave; Nội từ 50 đến 80 km.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ngo&amp;agrave;i 10 trường THPT kể tr&amp;ecirc;n, năm nay H&amp;agrave; Nội c&amp;ograve;n c&amp;oacute; 19 trường kh&amp;aacute;c, đều thuộc ngoại th&amp;agrave;nh, lấy điểm chuẩn dưới 30, tức chưa đến 5 điểm mỗi m&amp;ocirc;n. Trong đ&amp;oacute; khu vực 12 c&amp;oacute; 7 trường, khu vực 9 v&amp;agrave; khu vực 11 (Thường T&amp;iacute;n, Ph&amp;uacute; Xuy&amp;ecirc;n) mỗi nơi c&amp;oacute; 5 trường. Cụ thể như sau:&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;10 trường THPT ở H&agrave; Nội lấy 3-4 điểm mỗi m&ocirc;n&quot; src=&quot;https://i1-vnexpress.vnecdn.net/2021/06/28/4-PNG-1182-1624889414.png?w=680&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=fG0V6-1YIBRvKD2ZhEJwPw&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Ch&amp;ecirc;nh lệch điểm chuẩn v&amp;agrave;o lớp 10 của H&amp;agrave; Nội diễn ra v&amp;agrave;i chục năm nay, đặc biệt sau khi H&amp;agrave; Nội mở rộng v&amp;agrave;o năm 2008. Việc tổ chức thi tuyển thay v&amp;igrave; x&amp;eacute;t tuyển kết hợp với thi tuyển như c&amp;aacute;ch đ&amp;acirc;y 3 năm v&amp;agrave; thi th&amp;ecirc;m m&amp;ocirc;n Ngoại ngữ c&amp;agrave;ng k&amp;eacute;o rộng khoảng c&amp;aacute;ch điểm chuẩn giữa v&amp;ugrave;ng trung t&amp;acirc;m th&amp;agrave;nh phố với huyện ngoại th&amp;agrave;nh xa x&amp;ocirc;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;a href=&quot;https://vnexpress.net/vi-sao-diem-chuan-vao-lop-10-o-ha-noi-qua-chenh-lech-3940072.html&quot; rel=&quot;dofollow&quot;&gt;L&amp;yacute; giải&amp;nbsp;&lt;/a&gt;việc ch&amp;ecirc;nh lệch điểm chuẩn, nhiều nh&amp;agrave; gi&amp;aacute;o cho rằng c&amp;oacute; ba nguy&amp;ecirc;n nh&amp;acirc;n ch&amp;iacute;nh. Thứ nhất l&amp;agrave; chất lượng đội ngũ gi&amp;aacute;o vi&amp;ecirc;n ch&amp;ecirc;nh lệch, gi&amp;aacute;o vi&amp;ecirc;n giỏi ở v&amp;ugrave;ng ven, n&amp;ocirc;ng th&amp;ocirc;n đổ dồn về trung t&amp;acirc;m. Thứ hai l&amp;agrave; điều kiện cơ sở vật chất, m&amp;ocirc;i trường học tập ở một số huyện c&amp;ograve;n kh&amp;oacute; khăn. Cuối c&amp;ugrave;ng l&amp;agrave; do đời sống kh&amp;oacute; khăn, phụ huynh chưa chăm lo, quan t&amp;acirc;m đến việc học tập của con em.&lt;/p&gt;\r\n\r\n&lt;p&gt;Sau hai lần l&amp;ugrave;i thi do Covid-19, ng&amp;agrave;y 12-13/6, hơn 93.000 th&amp;iacute; sinh H&amp;agrave; Nội thi 4 m&amp;ocirc;n Ngữ văn, Ngoại ngữ, To&amp;aacute;n, Lịch sử để gi&amp;agrave;nh 67.000 suất v&amp;agrave;o lớp 10 c&amp;ocirc;ng lập. 26.000 em c&amp;ograve;n lại sẽ học trường ngo&amp;agrave;i c&amp;ocirc;ng lập, trường c&amp;ocirc;ng lập tự chủ t&amp;agrave;i ch&amp;iacute;nh, trung t&amp;acirc;m Gi&amp;aacute;o dục nghề nghiệp - Gi&amp;aacute;o dục thường xuy&amp;ecirc;n v&amp;agrave; học nghề.&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&amp;iacute; sinh được đăng k&amp;yacute; tối đa 3 nguyện vọng, nhiều hơn năm ngo&amp;aacute;i 1. Nếu trượt nguyện vọng 1, th&amp;iacute; sinh sẽ được x&amp;eacute;t tuyển nguyện vọng 2, nhưng phải c&amp;oacute; điểm cao hơn điểm chuẩn của trường &amp;iacute;t nhất 1 điểm. Nếu kh&amp;ocirc;ng tr&amp;uacute;ng tuyển nguyện vọng 2, c&amp;aacute;c em được x&amp;eacute;t tuyển nguyện vọng 3, nhưng phải c&amp;oacute; điểm cao hơn điểm chuẩn nguyện vọng 1 của trường &amp;iacute;t nhất 2 điểm.&lt;/p&gt;\r\n', '10-truong-thpt-o-ha-noi-lay-3-4-diem-moi-mon', './uploads/images/1624905574_10-trng-thpt-ly-im-chun.png', 1, 6, '2021-06-29 01:39:34', '2021-06-28 18:47:36');

